/**
 * TimelineGantt Component
 *
 * Interactive Gantt chart visualization for project timeline using react-calendar-timeline.
 * Displays phases as horizontal bars, milestones as markers, and dependencies.
 *
 * Features:
 * - Drag-and-drop phase duration adjustment
 * - Zoom controls (day/week/month view)
 * - Phase dependencies visualization
 * - Milestone markers
 * - Responsive design
 *
 * API Integration: /api/v1/plan/roadmap (consolidated endpoint)
 * Field Naming: snake_case (per CLAUDE.md)
 *
 * Generated by CC - Claude Code
 */

import React, { useMemo, useState } from 'react';
import Timeline, {
  TimelineHeaders,
  DateHeader,
  SidebarHeader,
  type TimelineGroup,
  type TimelineItem,
} from 'react-calendar-timeline';
import moment from 'moment';
import { ZoomIn, ZoomOut, Maximize2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert } from '@/components/ui/alert';
import { Card } from '@/components/ui/card';

// Import styles for react-calendar-timeline
import 'react-calendar-timeline/dist/style.css';

// =============================================================================
// TypeScript Interfaces (Using snake_case per CLAUDE.md)
// =============================================================================

/**
 * Timeline phase data from backend API.
 * Matches TimelinePhase interface from useTimeline hook.
 */
export interface TimelinePhase {
  id: string;
  name: string;
  start_date: string;
  end_date: string;
  status: 'Not Started' | 'In Progress' | 'Completed' | 'Delayed';
  progress: number;
  dependencies: string[];
  wave_number?: number; // Optional wave grouping
  phase_number?: number; // Phase sequence number
  is_on_critical_path?: boolean; // Critical path indicator from backend
  milestones: Array<{
    name: string;
    date: string;
    status: 'Pending' | 'Completed' | 'At Risk';
    description: string;
  }>;
  risks: Array<{
    description: string;
    impact: 'High' | 'Medium' | 'Low';
    mitigation: string;
  }>;
}

/**
 * Timeline data structure from API.
 */
export interface TimelineData {
  phases: TimelinePhase[];
  metrics: {
    total_duration_weeks: number;
    completed_phases: number;
    total_phases: number;
    overall_progress: number;
    delayed_milestones: number;
    at_risk_milestones: number;
  };
  critical_path: string[];
  schedule_health: {
    status: 'On Track' | 'At Risk' | 'Delayed';
    issues: string[];
    recommendations: string[];
  };
}

/**
 * Zoom level options for timeline view.
 */
type ZoomLevel = 'day' | 'week' | 'month';

/**
 * Props for TimelineGantt component.
 */
interface TimelineGanttProps {
  /** Timeline data from API */
  data: TimelineData;
  /** Callback when phase dates are updated via drag/resize */
  onPhaseUpdate?: (phaseId: string, startDate: Date, endDate: Date) => void;
  /** Show loading state */
  isLoading?: boolean;
}

// =============================================================================
// Helper Functions
// =============================================================================

/**
 * Get color for phase status badge.
 */
const getStatusColor = (status: string): string => {
  const colors: Record<string, string> = {
    'Not Started': '#9CA3AF',
    'In Progress': '#3B82F6',
    'Completed': '#10B981',
    'Delayed': '#EF4444',
    'At Risk': '#F59E0B',
    'Pending': '#8B5CF6',
    'On Track': '#10B981'
  };
  return colors[status] || '#9CA3AF';
};

/**
 * Calculate time range based on zoom level.
 */
const getTimeRange = (
  startDate: Date,
  endDate: Date,
  zoom: ZoomLevel
): { visibleTimeStart: number; visibleTimeEnd: number } => {
  const start = moment(startDate);
  const end = moment(endDate);
  const duration = end.diff(start, 'days');

  // Add padding based on zoom level
  const padding = {
    day: 7,
    week: 14,
    month: 30,
  }[zoom];

  return {
    visibleTimeStart: start.subtract(padding, 'days').valueOf(),
    visibleTimeEnd: end.add(padding, 'days').valueOf(),
  };
};

/**
 * Get zoom configuration for timeline.
 */
const getZoomConfig = (zoom: ZoomLevel) => {
  const configs = {
    day: {
      defaultTimeStart: moment().startOf('day').add(-3, 'days'),
      defaultTimeEnd: moment().startOf('day').add(7, 'days'),
      minZoom: 1000 * 60 * 60 * 24, // 1 day
      maxZoom: 1000 * 60 * 60 * 24 * 7, // 1 week
    },
    week: {
      defaultTimeStart: moment().startOf('week').add(-1, 'weeks'),
      defaultTimeEnd: moment().startOf('week').add(4, 'weeks'),
      minZoom: 1000 * 60 * 60 * 24 * 7, // 1 week
      maxZoom: 1000 * 60 * 60 * 24 * 30, // 30 days
    },
    month: {
      defaultTimeStart: moment().startOf('month').add(-1, 'months'),
      defaultTimeEnd: moment().startOf('month').add(3, 'months'),
      minZoom: 1000 * 60 * 60 * 24 * 30, // 30 days
      maxZoom: 1000 * 60 * 60 * 24 * 90, // 90 days
    },
  };
  return configs[zoom];
};

// =============================================================================
// TimelineGantt Component
// =============================================================================

export const TimelineGantt: React.FC<TimelineGanttProps> = ({
  data,
  onPhaseUpdate,
  isLoading = false,
}) => {
  const [zoomLevel, setZoomLevel] = useState<ZoomLevel>('week');
  const [visibleTimeStart, setVisibleTimeStart] = useState<number>(
    moment().startOf('week').add(-1, 'weeks').valueOf()
  );
  const [visibleTimeEnd, setVisibleTimeEnd] = useState<number>(
    moment().startOf('week').add(4, 'weeks').valueOf()
  );
  const [selectedPhaseId, setSelectedPhaseId] = useState<string | null>(null);

  // Helper: Check if phase is on critical path
  const isOnCriticalPath = (phase: TimelinePhase): boolean => {
    // Check backend flag first
    if (phase.is_on_critical_path !== undefined) {
      return phase.is_on_critical_path;
    }
    // Fallback: Check if phase ID or name is in critical_path array
    return data.critical_path.some(
      (cp) => cp === phase.id || cp === phase.name
    );
  };

  // Group phases by wave for visual organization
  const phasesByWave = useMemo(() => {
    const grouped = new Map<number, TimelinePhase[]>();
    data.phases.forEach((phase) => {
      const wave = phase.wave_number || 0; // Default to wave 0 if not specified
      if (!grouped.has(wave)) {
        grouped.set(wave, []);
      }
      grouped.get(wave).push(phase);
    });
    return grouped;
  }, [data.phases]);

  // Transform phases to timeline groups and items
  const { groups, items } = useMemo(() => {
    const groups: TimelineGroup[] = [];
    const items: TimelineItem[] = [];

    // Sort waves for consistent ordering
    const sortedWaves = Array.from(phasesByWave.keys()).sort((a, b) => a - b);

    sortedWaves.forEach((waveNum) => {
      const wavePhasesData = phasesByWave.get(waveNum);

      // Add wave separator group (visual only)
      if (waveNum > 0) {
        groups.push({
          id: `wave-separator-${waveNum}`,
          title: `‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Wave ${waveNum} ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ`,
          rightTitle: '',
        });
      }

      // Add phases for this wave
      wavePhasesData.forEach((phase) => {
        const isCritical = isOnCriticalPath(phase);

        groups.push({
          id: phase.id,
          title: phase.name,
          rightTitle: `${phase.progress}%`,
        });

        // Create phase bar with progress indicator
        const baseColor = getStatusColor(phase.status);
        const criticalBorder = isCritical ? '3px solid #EF4444' : 'none';

        items.push({
          id: `phase-${phase.id}`,
          group: phase.id,
          title: `${isCritical ? '‚ö†Ô∏è ' : ''}${phase.name}`,
          start_time: moment(phase.start_date).valueOf(),
          end_time: moment(phase.end_date).valueOf(),
          canMove: true,
          canResize: 'both',
          itemProps: {
            style: {
              background: `linear-gradient(to right, ${baseColor} 0%, ${baseColor} ${phase.progress}%, rgba(0,0,0,0.05) ${phase.progress}%, rgba(0,0,0,0.05) 100%)`,
              borderRadius: '4px',
              border: criticalBorder,
              boxShadow: isCritical ? '0 2px 8px rgba(239, 68, 68, 0.3)' : 'none',
              fontWeight: isCritical ? 'bold' : 'normal',
              cursor: 'pointer',
            },
            onClick: () => setSelectedPhaseId(phase.id),
          },
        });

        // Add milestones as diamond markers
        phase.milestones.forEach((milestone, idx) => {
          items.push({
            id: `milestone-${phase.id}-${idx}`,
            group: phase.id,
            title: `üèÅ ${milestone.name}`,
            start_time: moment(milestone.date).valueOf(),
            end_time: moment(milestone.date).add(1, 'hour').valueOf(),
            canMove: false,
            canResize: false,
            itemProps: {
              style: {
                background: getStatusColor(milestone.status),
                borderRadius: '50%',
                border: '2px solid #fff',
                height: '24px',
                width: '24px',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
              },
            },
          });
        });
      });
    });

    return { groups, items };
  }, [data.phases, data.critical_path, phasesByWave]);

  // Handle item move (drag)
  const handleItemMove = (itemId: string, dragTime: number, newGroupOrder: number) => {
    const item = items.find((i) => i.id === itemId);
    if (!item || !onPhaseUpdate) return;

    // Only allow moving phase items, not milestones
    if (itemId.startsWith('milestone-')) return;

    const phaseId = itemId.replace('phase-', '');
    const duration = item.end_time - item.start_time;
    const newStartDate = new Date(dragTime);
    const newEndDate = new Date(dragTime + duration);

    onPhaseUpdate(phaseId, newStartDate, newEndDate);
  };

  // Handle item resize
  const handleItemResize = (
    itemId: string,
    time: number,
    edge: 'left' | 'right'
  ) => {
    const item = items.find((i) => i.id === itemId);
    if (!item || !onPhaseUpdate) return;

    // Only allow resizing phase items, not milestones
    if (itemId.startsWith('milestone-')) return;

    const phaseId = itemId.replace('phase-', '');
    const newStartDate = edge === 'left' ? new Date(time) : new Date(item.start_time);
    const newEndDate = edge === 'right' ? new Date(time) : new Date(item.end_time);

    onPhaseUpdate(phaseId, newStartDate, newEndDate);
  };

  // Zoom in/out handlers
  const handleZoomIn = () => {
    const currentRange = visibleTimeEnd - visibleTimeStart;
    const newRange = currentRange * 0.75;
    const center = visibleTimeStart + currentRange / 2;

    setVisibleTimeStart(center - newRange / 2);
    setVisibleTimeEnd(center + newRange / 2);
  };

  const handleZoomOut = () => {
    const currentRange = visibleTimeEnd - visibleTimeStart;
    const newRange = currentRange * 1.25;
    const center = visibleTimeStart + currentRange / 2;

    setVisibleTimeStart(center - newRange / 2);
    setVisibleTimeEnd(center + newRange / 2);
  };

  const handleFitToWindow = () => {
    if (data.phases.length === 0) return;

    const allDates = data.phases.flatMap((phase) => [
      moment(phase.start_date).valueOf(),
      moment(phase.end_date).valueOf(),
    ]);

    const minDate = Math.min(...allDates);
    const maxDate = Math.max(...allDates);
    const padding = (maxDate - minDate) * 0.1; // 10% padding

    setVisibleTimeStart(minDate - padding);
    setVisibleTimeEnd(maxDate + padding);
  };

  // Empty state
  if (!data.phases || data.phases.length === 0) {
    return (
      <Card className="p-8">
        <Alert variant="info">
          <p>No timeline data available. Start planning to generate timeline phases.</p>
        </Alert>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Controls */}
      <div className="flex items-center justify-between bg-white p-4 rounded-lg shadow">
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium text-gray-700">View:</span>
          <div className="flex space-x-1">
            {(['day', 'week', 'month'] as ZoomLevel[]).map((level) => (
              <Button
                key={level}
                variant={zoomLevel === level ? 'default' : 'outline'}
                size="sm"
                onClick={() => setZoomLevel(level)}
              >
                {level.charAt(0).toUpperCase() + level.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        <div className="flex items-center space-x-2">
          <Button variant="outline" size="sm" onClick={handleZoomIn}>
            <ZoomIn className="h-4 w-4 mr-1" />
            Zoom In
          </Button>
          <Button variant="outline" size="sm" onClick={handleZoomOut}>
            <ZoomOut className="h-4 w-4 mr-1" />
            Zoom Out
          </Button>
          <Button variant="outline" size="sm" onClick={handleFitToWindow}>
            <Maximize2 className="h-4 w-4 mr-1" />
            Fit to Window
          </Button>
        </div>
      </div>

      {/* Legend */}
      <div className="flex items-center justify-between bg-white p-4 rounded-lg shadow">
        <div className="flex items-center space-x-4">
          <span className="text-sm font-medium text-gray-700">Status:</span>
          {['Not Started', 'In Progress', 'Completed', 'Delayed'].map((status) => (
            <div key={status} className="flex items-center space-x-2">
              <div
                className="w-4 h-4 rounded"
                style={{ backgroundColor: getStatusColor(status) }}
              />
              <span className="text-sm text-gray-600">{status}</span>
            </div>
          ))}
        </div>
        <div className="flex items-center space-x-4 border-l pl-4">
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 rounded border-2 border-red-500" />
            <span className="text-sm text-gray-600">Critical Path</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 rounded-full bg-blue-600" />
            <span className="text-sm text-gray-600">Milestone</span>
          </div>
        </div>
      </div>

      {/* Gantt Chart */}
      <Card className="p-4 overflow-hidden">
        <div style={{ height: '600px' }}>
          <Timeline
            groups={groups}
            items={items}
            visibleTimeStart={visibleTimeStart}
            visibleTimeEnd={visibleTimeEnd}
            onTimeChange={(
              visibleTimeStart: number,
              visibleTimeEnd: number,
              updateScrollCanvas: (start: number, end: number) => void
            ) => {
              setVisibleTimeStart(visibleTimeStart);
              setVisibleTimeEnd(visibleTimeEnd);
              updateScrollCanvas(visibleTimeStart, visibleTimeEnd);
            }}
            onItemMove={handleItemMove}
            onItemResize={handleItemResize}
            canMove={true}
            canResize="both"
            stackItems={true}
            lineHeight={60}
            itemHeightRatio={0.75}
            minZoom={getZoomConfig(zoomLevel).minZoom}
            maxZoom={getZoomConfig(zoomLevel).maxZoom}
          >
            <TimelineHeaders>
              <SidebarHeader>
                {({ getRootProps }) => (
                  <div {...getRootProps()} className="bg-gray-100 p-2 font-semibold">
                    Phase
                  </div>
                )}
              </SidebarHeader>
              <DateHeader unit="primaryHeader" />
              <DateHeader />
            </TimelineHeaders>
          </Timeline>
        </div>
      </Card>

      {/* Critical Path Indicator */}
      {data.critical_path && data.critical_path.length > 0 && (
        <Card className="p-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm font-medium text-gray-700">Critical Path:</span>
            {data.critical_path.map((phase, index) => (
              <React.Fragment key={index}>
                <Badge variant="outline">{phase}</Badge>
                {index < data.critical_path.length - 1 && (
                  <span className="text-gray-400">‚Üí</span>
                )}
              </React.Fragment>
            ))}
          </div>
        </Card>
      )}

      {/* Phase Details Modal */}
      {selectedPhaseId && (() => {
        const selectedPhase = data.phases.find((p) => p.id === selectedPhaseId);
        if (!selectedPhase) return null;

        const isCritical = isOnCriticalPath(selectedPhase);

        return (
          <Card className="fixed bottom-8 right-8 w-96 shadow-2xl z-50 animate-in slide-in-from-bottom">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  {isCritical && '‚ö†Ô∏è '}
                  {selectedPhase.name}
                </h3>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSelectedPhaseId(null)}
                >
                  ‚úï
                </Button>
              </div>

              {isCritical && (
                <Alert variant="warning" className="mb-4">
                  <Flag className="h-4 w-4" />
                  <p className="text-sm">This phase is on the critical path</p>
                </Alert>
              )}

              <div className="space-y-3">
                <div>
                  <span className="text-sm font-medium text-gray-600">Status:</span>
                  <Badge className={`ml-2 ${getStatusColor(selectedPhase.status)}`}>
                    {selectedPhase.status}
                  </Badge>
                </div>

                <div>
                  <span className="text-sm font-medium text-gray-600">Progress:</span>
                  <div className="flex items-center space-x-2 mt-1">
                    <Progress value={selectedPhase.progress} className="flex-1" />
                    <span className="text-sm text-gray-900 font-medium">
                      {selectedPhase.progress}%
                    </span>
                  </div>
                </div>

                <div>
                  <span className="text-sm font-medium text-gray-600">Timeline:</span>
                  <p className="text-sm text-gray-900 mt-1">
                    {new Date(selectedPhase.start_date).toLocaleDateString()} -{' '}
                    {new Date(selectedPhase.end_date).toLocaleDateString()}
                  </p>
                </div>

                {selectedPhase.dependencies.length > 0 && (
                  <div>
                    <span className="text-sm font-medium text-gray-600">Dependencies:</span>
                    <div className="flex flex-wrap gap-1 mt-1">
                      {selectedPhase.dependencies.map((dep, idx) => (
                        <Badge key={idx} variant="outline" className="text-xs">
                          {dep}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}

                {selectedPhase.milestones.length > 0 && (
                  <div>
                    <span className="text-sm font-medium text-gray-600">Milestones:</span>
                    <div className="space-y-2 mt-1">
                      {selectedPhase.milestones.map((milestone, idx) => (
                        <div key={idx} className="flex items-center justify-between text-sm">
                          <span className="text-gray-900">üèÅ {milestone.name}</span>
                          <Badge className={`${getStatusColor(milestone.status)}`}>
                            {milestone.status}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {selectedPhase.risks.length > 0 && (
                  <div>
                    <span className="text-sm font-medium text-gray-600">Risks:</span>
                    <div className="space-y-2 mt-1">
                      {selectedPhase.risks.map((risk, idx) => (
                        <div key={idx} className="bg-gray-50 p-2 rounded text-sm">
                          <div className="flex items-center justify-between mb-1">
                            <span className="text-gray-900 font-medium">
                              {risk.description}
                            </span>
                            <Badge variant="outline">{risk.impact} Impact</Badge>
                          </div>
                          <p className="text-gray-600 text-xs">
                            Mitigation: {risk.mitigation}
                          </p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </Card>
        );
      })()}
    </div>
  );
};

export default TimelineGantt;
