/**
 * Wave Card Component with Drag-Drop Support
 *
 * Individual wave card that displays wave metadata and assigned applications.
 * Supports drag-drop for reassigning applications between waves using @dnd-kit.
 *
 * Uses snake_case field names per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React from 'react';
import { useDroppable } from '@dnd-kit/core';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import type { Wave } from '@/lib/api/planningFlowService';
import { Calendar, Package, GripVertical } from 'lucide-react';

interface WaveCardProps {
  /** Wave data to display */
  wave: Wave;
  /** Array of application details (if available) */
  applications?: Array<{ id: string; name: string; }>;
  /** Callback when user clicks edit */
  onEdit: (wave: Wave) => void;
  /** Callback when user clicks delete */
  onDelete: (wave: Wave) => void;
  /** Whether drag-drop is enabled */
  isDragEnabled?: boolean;
}

/**
 * Get badge color based on wave status.
 */
const getStatusColor = (status: string): string => {
  switch (status) {
    case 'planned':
      return 'bg-blue-100 text-blue-800';
    case 'in_progress':
      return 'bg-yellow-100 text-yellow-800';
    case 'completed':
      return 'bg-green-100 text-green-800';
    case 'blocked':
      return 'bg-red-100 text-red-800';
    default:
      return 'bg-gray-100 text-gray-800';
  }
};

/**
 * Format status text for display.
 */
const formatStatus = (status: string): string => {
  return status.replace('_', ' ').replace(/\b\w/g, (l) => l.toUpperCase());
};

/**
 * Draggable Application Item within a wave.
 */
function ApplicationItem({
  app,
  waveNumber,
  isDragEnabled
}: {
  app: { id: string; name: string };
  waveNumber: number;
  isDragEnabled?: boolean;
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({
    id: `app-${app.id}`,
    data: {
      type: 'application',
      app_id: app.id,
      source_wave: waveNumber,
    },
    disabled: !isDragEnabled,
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className={`
        flex items-center gap-2 p-2 bg-gray-50 rounded border border-gray-200
        ${isDragEnabled ? 'cursor-grab active:cursor-grabbing hover:bg-gray-100' : ''}
        ${isDragging ? 'shadow-lg' : ''}
      `}
      {...attributes}
      {...listeners}
    >
      {isDragEnabled && <GripVertical className="h-4 w-4 text-gray-400" />}
      <Package className="h-4 w-4 text-gray-500" />
      <span className="text-sm text-gray-700 truncate">{app.name}</span>
    </div>
  );
}

/**
 * Wave Card Component with drag-drop support.
 */
export default function WaveCard({
  wave,
  applications = [],
  onEdit,
  onDelete,
  isDragEnabled = true,
}: WaveCardProps): JSX.Element {
  // Set up droppable zone for this wave
  const { setNodeRef, isOver } = useDroppable({
    id: `wave-${wave.wave_number}`,
    data: {
      type: 'wave',
      wave_number: wave.wave_number,
    },
  });

  return (
    <div
      ref={setNodeRef}
      className={`
        border border-gray-200 rounded-lg p-6 bg-white
        transition-all duration-200
        ${isOver ? 'ring-2 ring-blue-500 shadow-lg' : 'hover:shadow-lg'}
      `}
    >
      {/* Header */}
      <div className="flex justify-between items-start mb-4">
        <div className="flex-1">
          <h3 className="text-xl font-semibold text-gray-900">
            {wave.wave_name}
          </h3>
          <p className="text-sm text-gray-500 mt-1">Wave {wave.wave_number}</p>
        </div>
        <div className="flex gap-2">
          <button
            onClick={() => onEdit(wave)}
            className="text-blue-600 hover:text-blue-800 text-sm font-medium transition-colors"
            title="Edit wave"
          >
            Edit
          </button>
          <button
            onClick={() => onDelete(wave)}
            className="text-red-600 hover:text-red-800 text-sm font-medium transition-colors"
            title="Delete wave"
          >
            Delete
          </button>
        </div>
      </div>

      {/* Description */}
      {wave.description && (
        <p className="text-sm text-gray-600 mb-4 line-clamp-2">
          {wave.description}
        </p>
      )}

      {/* Wave Metadata */}
      <div className="space-y-2 mb-4">
        {/* Application Count */}
        <div className="flex justify-between items-center text-sm">
          <span className="text-gray-600">Applications:</span>
          <span className="font-semibold text-gray-900">
            {applications.length || wave.application_count || 0}
          </span>
        </div>

        {/* Dates */}
        {(wave.start_date || wave.end_date) && (
          <div className="flex items-center gap-2 text-sm text-gray-600">
            <Calendar className="h-4 w-4" />
            <span>
              {wave.start_date && new Date(wave.start_date).toLocaleDateString()}
              {wave.start_date && wave.end_date && ' - '}
              {wave.end_date && new Date(wave.end_date).toLocaleDateString()}
            </span>
          </div>
        )}

        {/* Duration */}
        {wave.start_date && wave.end_date && (
          <div className="flex justify-between items-center text-sm">
            <span className="text-gray-600">Duration:</span>
            <span className="font-medium text-gray-900">
              {Math.ceil(
                (new Date(wave.end_date).getTime() -
                  new Date(wave.start_date).getTime()) /
                  (1000 * 60 * 60 * 24)
              )}{' '}
              days
            </span>
          </div>
        )}

        {/* Status Badge */}
        <div className="flex justify-between items-center text-sm pt-2 border-t border-gray-100">
          <span className="text-gray-600">Status:</span>
          <span
            className={`px-2 py-1 rounded text-xs font-medium ${getStatusColor(
              wave.status
            )}`}
          >
            {formatStatus(wave.status)}
          </span>
        </div>
      </div>

      {/* Applications List (Draggable) */}
      {applications.length > 0 && (
        <div className="mt-4 pt-4 border-t border-gray-100">
          <h4 className="text-sm font-medium text-gray-700 mb-2">
            Assigned Applications
          </h4>
          <div className="space-y-2 max-h-48 overflow-y-auto">
            {applications.map((app) => (
              <ApplicationItem
                key={app.id}
                app={app}
                waveNumber={wave.wave_number}
                isDragEnabled={isDragEnabled}
              />
            ))}
          </div>
        </div>
      )}

      {/* Drop Zone Indicator */}
      {isOver && applications.length === 0 && (
        <div className="mt-4 pt-4 border-t border-gray-100">
          <div className="border-2 border-dashed border-blue-300 rounded-lg p-4 text-center">
            <p className="text-sm text-blue-600">Drop application here</p>
          </div>
        </div>
      )}

      {/* Empty State for Applications */}
      {!isOver && applications.length === 0 && (
        <div className="mt-4 pt-4 border-t border-gray-100">
          <div className="text-center text-sm text-gray-500">
            No applications assigned yet
          </div>
        </div>
      )}
    </div>
  );
}
