/**
 * Wave Dashboard Component
 *
 * Displays migration waves in a grid layout with summary information.
 * Provides actions for editing and deleting waves.
 *
 * Uses snake_case field names per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React from 'react';
import type { Wave } from '@/lib/api/planningFlowService';

interface WaveDashboardProps {
  /** Array of waves to display */
  waves: Wave[];
  /** Callback when user clicks edit on a wave */
  onEditWave: (wave: Wave) => void;
  /** Callback when user clicks delete on a wave */
  onDeleteWave: (wave: Wave) => void;
}

/**
 * Get badge color based on wave status.
 */
const getStatusColor = (status: string): string => {
  switch (status) {
    case 'planned':
      return 'bg-blue-100 text-blue-800';
    case 'in_progress':
      return 'bg-yellow-100 text-yellow-800';
    case 'completed':
      return 'bg-green-100 text-green-800';
    case 'blocked':
      return 'bg-red-100 text-red-800';
    default:
      return 'bg-gray-100 text-gray-800';
  }
};

/**
 * Format status text for display.
 */
const formatStatus = (status: string): string => {
  return status.replace('_', ' ').replace(/\b\w/g, (l) => l.toUpperCase());
};

/**
 * Wave Dashboard Component.
 */
export default function WaveDashboard({
  waves,
  onEditWave,
  onDeleteWave,
}: WaveDashboardProps): JSX.Element {
  // Empty state
  if (waves.length === 0) {
    return (
      <div className="border-2 border-dashed border-gray-300 rounded-lg p-12 text-center">
        <svg
          className="mx-auto h-12 w-12 text-gray-400 mb-4"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"
          />
        </svg>
        <p className="text-gray-500 text-lg font-medium">No waves created yet</p>
        <p className="text-gray-400 text-sm mt-2">
          Click "Create Wave" to organize applications into migration waves
        </p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {waves.map((wave) => (
        <div
          key={wave.wave_number}
          className="border border-gray-200 rounded-lg p-6 hover:shadow-lg transition-shadow bg-white"
        >
          {/* Header */}
          <div className="flex justify-between items-start mb-4">
            <div className="flex-1">
              <h3 className="text-xl font-semibold text-gray-900">
                {wave.wave_name}
              </h3>
              <p className="text-sm text-gray-500 mt-1">Wave {wave.wave_number}</p>
            </div>
            <div className="flex gap-2">
              <button
                onClick={() => onEditWave(wave)}
                className="text-blue-600 hover:text-blue-800 text-sm font-medium transition-colors"
                title="Edit wave"
              >
                Edit
              </button>
              <button
                onClick={() => onDeleteWave(wave)}
                className="text-red-600 hover:text-red-800 text-sm font-medium transition-colors"
                title="Delete wave"
              >
                Delete
              </button>
            </div>
          </div>

          {/* Description (if exists) */}
          {wave.description && (
            <p className="text-sm text-gray-600 mb-4 line-clamp-2">
              {wave.description}
            </p>
          )}

          {/* Wave Details */}
          <div className="space-y-3">
            {/* Application Count */}
            <div className="flex justify-between items-center text-sm">
              <span className="text-gray-600">Applications:</span>
              <span className="font-semibold text-gray-900">
                {wave.application_count}
              </span>
            </div>

            {/* Start Date */}
            {wave.start_date && (
              <div className="flex justify-between items-center text-sm">
                <span className="text-gray-600">Start Date:</span>
                <span className="font-medium text-gray-900">
                  {new Date(wave.start_date).toLocaleDateString()}
                </span>
              </div>
            )}

            {/* End Date */}
            {wave.end_date && (
              <div className="flex justify-between items-center text-sm">
                <span className="text-gray-600">End Date:</span>
                <span className="font-medium text-gray-900">
                  {new Date(wave.end_date).toLocaleDateString()}
                </span>
              </div>
            )}

            {/* Duration (if both dates exist) */}
            {wave.start_date && wave.end_date && (
              <div className="flex justify-between items-center text-sm">
                <span className="text-gray-600">Duration:</span>
                <span className="font-medium text-gray-900">
                  {Math.ceil(
                    (new Date(wave.end_date).getTime() -
                      new Date(wave.start_date).getTime()) /
                      (1000 * 60 * 60 * 24)
                  )}{' '}
                  days
                </span>
              </div>
            )}

            {/* Dependencies */}
            {wave.dependencies && wave.dependencies.length > 0 && (
              <div className="flex justify-between items-center text-sm">
                <span className="text-gray-600">Dependencies:</span>
                <span className="font-medium text-gray-900">
                  {wave.dependencies.map((dep) => `Wave ${dep}`).join(', ')}
                </span>
              </div>
            )}

            {/* Status Badge */}
            <div className="flex justify-between items-center text-sm pt-2 border-t border-gray-100">
              <span className="text-gray-600">Status:</span>
              <span
                className={`px-2 py-1 rounded text-xs font-medium ${getStatusColor(
                  wave.status
                )}`}
              >
                {formatStatus(wave.status)}
              </span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
