/**
 * Wave Modal Component
 *
 * Modal dialog for creating or editing migration waves.
 * Supports wave name, dates, status, and description.
 *
 * Uses snake_case field names per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React, { useState, useEffect } from 'react';
import type { FormEvent } from 'react';
import type { Wave } from '@/lib/api/planningFlowService';

interface WaveModalProps {
  /** Wave to edit (null for create new) */
  wave: Wave | null;
  /** Callback when user saves the wave */
  onSave: (wave: Partial<Wave>) => void;
  /** Callback when user closes the modal */
  onClose: () => void;
}

/**
 * Wave Modal Component.
 */
export default function WaveModal({ wave, onSave, onClose }: WaveModalProps): JSX.Element {
  // Form state using snake_case field names
  const [wave_name, setWaveName] = useState('');
  const [start_date, setStartDate] = useState('');
  const [end_date, setEndDate] = useState('');
  const [status, setStatus] = useState<Wave['status']>('planned');
  const [description, setDescription] = useState('');

  // Initialize form from wave prop
  useEffect(() => {
    if (wave) {
      setWaveName(wave.wave_name);
      // Convert ISO datetime to YYYY-MM-DD for date inputs
      setStartDate(wave.start_date ? wave.start_date.split('T')[0] : '');
      setEndDate(wave.end_date ? wave.end_date.split('T')[0] : '');
      setStatus(wave.status);
      setDescription(wave.description || '');
    } else {
      // Reset form for new wave
      setWaveName('');
      setStartDate('');
      setEndDate('');
      setStatus('planned');
      setDescription('');
    }
  }, [wave]);

  /**
   * Handle form submission.
   * Validates dates and calls onSave with snake_case fields.
   */
  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();

    // Validate wave name
    if (!wave_name.trim()) {
      alert('Wave name is required');
      return;
    }

    // Validate dates if both are provided
    if (start_date && end_date) {
      const startDateTime = new Date(start_date).getTime();
      const endDateTime = new Date(end_date).getTime();

      if (endDateTime <= startDateTime) {
        alert('End date must be after start date');
        return;
      }
    }

    // Build wave data with snake_case fields
    const waveData: Partial<Wave> = {
      wave_name,
      status,
    };

    // Add optional fields - convert YYYY-MM-DD to ISO datetime
    if (start_date) {
      // Convert to ISO datetime with UTC timezone
      waveData.start_date = new Date(start_date + 'T00:00:00Z').toISOString();
    }
    if (end_date) {
      // Convert to ISO datetime with UTC timezone
      waveData.end_date = new Date(end_date + 'T23:59:59Z').toISOString();
    }
    if (description.trim()) waveData.description = description;

    // Preserve existing applications if editing
    if (wave) {
      waveData.applications = wave.applications;
      waveData.wave_number = wave.wave_number;
    }

    onSave(waveData);
  };

  /**
   * Handle modal backdrop click to close.
   */
  const handleBackdropClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
      onClick={handleBackdropClick}
    >
      <div
        className="bg-white rounded-lg shadow-xl p-8 max-w-md w-full max-h-[90vh] overflow-y-auto"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-900">
            {wave ? 'Edit Wave' : 'Create New Wave'}
          </h2>
          <p className="text-sm text-gray-600 mt-1">
            {wave
              ? 'Update wave details and schedule'
              : 'Define a new migration wave'}
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Wave Name */}
          <div>
            <label
              htmlFor="wave_name"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Wave Name *
            </label>
            <input
              type="text"
              id="wave_name"
              value={wave_name}
              onChange={(e) => setWaveName(e.target.value)}
              required
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="e.g., Wave 1 - Pilot Applications"
            />
          </div>

          {/* Description */}
          <div>
            <label
              htmlFor="description"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Description
            </label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows={3}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Optional description of wave scope and objectives"
            />
          </div>

          {/* Start Date */}
          <div>
            <label
              htmlFor="start_date"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Start Date
            </label>
            <input
              type="date"
              id="start_date"
              value={start_date}
              onChange={(e) => setStartDate(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* End Date */}
          <div>
            <label
              htmlFor="end_date"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              End Date
            </label>
            <input
              type="date"
              id="end_date"
              value={end_date}
              onChange={(e) => setEndDate(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Status */}
          <div>
            <label
              htmlFor="status"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Status
            </label>
            <select
              id="status"
              value={status}
              onChange={(e) => setStatus(e.target.value as Wave['status'])}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="planned">Planned</option>
              <option value="in_progress">In Progress</option>
              <option value="completed">Completed</option>
              <option value="blocked">Blocked</option>
            </select>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3 mt-6 pt-4 border-t border-gray-200">
            <button
              type="submit"
              className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors font-medium"
            >
              Save Wave
            </button>
            <button
              type="button"
              onClick={onClose}
              className="flex-1 bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors font-medium"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
