/**
 * Migration Roadmap Page
 *
 * Displays wave timeline and milestones from planning flow data.
 * Uses planning_flow_id from URL params to fetch wave data.
 *
 * All field names use snake_case per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import Sidebar from '../../components/Sidebar';
import { planningFlowApi } from '@/lib/api/planningFlowService';
import type { Wave, WaveApplication } from '@/lib/api/planningFlowService';
import {
  Calendar,
  CheckCircle,
  Clock,
  AlertCircle,
  ChevronRight,
  Layers,
  AppWindow,
  AlertTriangle,
  ArrowRight,
  Target
} from 'lucide-react';
import { useAuth } from '@/contexts/AuthContext';
import { Spinner } from '@/components/ui/spinner';
import ContextBreadcrumbs from '@/components/context/ContextBreadcrumbs';
import { SidebarProvider } from '@/components/ui/sidebar';
import PlanNavigation from '@/components/plan/PlanNavigation';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';

// Helper functions
const formatDate = (dateStr: string | undefined): string => {
  if (!dateStr) return 'TBD';
  try {
    return new Date(dateStr).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  } catch {
    return dateStr;
  }
};

const getStatusIcon = (status: string): JSX.Element => {
  switch (status?.toLowerCase()) {
    case 'completed':
      return <CheckCircle className="h-5 w-5 text-green-500" />;
    case 'in_progress':
    case 'in-progress':
      return <Clock className="h-5 w-5 text-blue-500" />;
    case 'blocked':
      return <AlertTriangle className="h-5 w-5 text-red-500" />;
    case 'planned':
    default:
      return <Target className="h-5 w-5 text-gray-400" />;
  }
};

const getStatusBadge = (status: string): JSX.Element => {
  const statusLower = status?.toLowerCase() || 'planned';
  const variants: Record<string, { bg: string; text: string; label: string }> = {
    'completed': { bg: 'bg-green-100', text: 'text-green-800', label: 'Completed' },
    'in_progress': { bg: 'bg-blue-100', text: 'text-blue-800', label: 'In Progress' },
    'in-progress': { bg: 'bg-blue-100', text: 'text-blue-800', label: 'In Progress' },
    'blocked': { bg: 'bg-red-100', text: 'text-red-800', label: 'Blocked' },
    'planned': { bg: 'bg-gray-100', text: 'text-gray-800', label: 'Planned' }
  };
  const variant = variants[statusLower] || variants['planned'];
  return (
    <span className={cn('px-2 py-1 rounded-full text-xs font-medium', variant.bg, variant.text)}>
      {variant.label}
    </span>
  );
};

const getRiskBadge = (risk: string | undefined): JSX.Element | null => {
  if (!risk) return null;
  const riskLower = risk.toLowerCase();
  const variants: Record<string, { bg: string; text: string }> = {
    'low': { bg: 'bg-green-50', text: 'text-green-700' },
    'medium': { bg: 'bg-yellow-50', text: 'text-yellow-700' },
    'high': { bg: 'bg-red-50', text: 'text-red-700' }
  };
  const variant = variants[riskLower] || variants['medium'];
  return (
    <span className={cn('px-2 py-0.5 rounded text-xs font-medium', variant.bg, variant.text)}>
      {risk} Risk
    </span>
  );
};

const getProgressPercentage = (status: string): number => {
  switch (status?.toLowerCase()) {
    case 'completed': return 100;
    case 'in_progress':
    case 'in-progress': return 50;
    case 'blocked': return 25;
    default: return 0;
  }
};

const Roadmap = (): JSX.Element => {
  const { isAuthenticated } = useAuth();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  // Get planning_flow_id from URL
  const planning_flow_id = searchParams.get('planning_flow_id');

  // Fetch planning status with wave data
  const {
    data: planningStatus,
    isLoading,
    error
  } = useQuery({
    queryKey: ['planning-status', planning_flow_id],
    queryFn: () => planningFlowApi.getPlanningStatus(planning_flow_id!),
    enabled: !!planning_flow_id && isAuthenticated,
    staleTime: 30000
  });

  const waves = planningStatus?.wave_plan_data?.waves || [];

  // Calculate totals
  const totalApps = waves.reduce((sum, w) => sum + (w.application_count || 0), 0);
  const completedWaves = waves.filter(w => w.status === 'completed').length;
  const inProgressWaves = waves.filter(w => w.status === 'in_progress' || w.status === 'in-progress').length;

  if (!isAuthenticated) {
    return <div>Please log in to view the roadmap.</div>;
  }

  // No planning_flow_id in URL - show prompt to select
  if (!planning_flow_id) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64">
            <main className="p-8">
              <ContextBreadcrumbs showContextSelector={true} />
              <div className="max-w-7xl mx-auto">
                <div className="mb-6">
                  <h1 className="text-3xl font-bold text-gray-900 mb-2">Migration Roadmap</h1>
                  <p className="text-gray-600">View timeline and milestones for migration waves</p>
                </div>

                <Card className="text-center py-12">
                  <CardContent>
                    <Calendar className="h-16 w-16 mx-auto text-gray-400 mb-4" />
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">No Planning Flow Selected</h3>
                    <p className="text-gray-600 mb-6 max-w-md mx-auto">
                      Start a planning flow to see your migration roadmap with waves, timelines, and application assignments.
                    </p>
                    <Button onClick={() => navigate('/plan/wave-planning')}>
                      Go to Wave Planning
                    </Button>
                  </CardContent>
                </Card>
              </div>
            </main>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  if (isLoading) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 flex items-center justify-center">
            <Spinner size="lg" />
          </div>
        </div>
      </SidebarProvider>
    );
  }

  if (error) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 p-8">
            <ContextBreadcrumbs showContextSelector={true} />
            <div className="max-w-7xl mx-auto">
              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <p className="text-red-800">Error loading roadmap data. Please try again later.</p>
              </div>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  return (
    <SidebarProvider>
      <div className="min-h-screen bg-gray-50 flex">
        <Sidebar />
        <div className="flex-1 ml-64">
          <main className="p-8">
            <ContextBreadcrumbs showContextSelector={true} />
            <div className="max-w-7xl mx-auto">
              {/* Header */}
              <div className="mb-6">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Migration Roadmap</h1>
                <p className="text-gray-600">View timeline and milestones for migration waves</p>
              </div>

              {/* Plan Navigation */}
              <PlanNavigation currentPage="roadmap" planningFlowId={planning_flow_id} />

              {/* Summary Stats */}
              <div className="grid grid-cols-4 gap-4 mb-8">
                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 bg-blue-100 rounded-lg">
                        <Layers className="h-6 w-6 text-blue-600" />
                      </div>
                      <div>
                        <p className="text-2xl font-bold text-gray-900">{waves.length}</p>
                        <p className="text-sm text-gray-500">Total Waves</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 bg-purple-100 rounded-lg">
                        <AppWindow className="h-6 w-6 text-purple-600" />
                      </div>
                      <div>
                        <p className="text-2xl font-bold text-gray-900">{totalApps}</p>
                        <p className="text-sm text-gray-500">Applications</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 bg-green-100 rounded-lg">
                        <CheckCircle className="h-6 w-6 text-green-600" />
                      </div>
                      <div>
                        <p className="text-2xl font-bold text-gray-900">{completedWaves}</p>
                        <p className="text-sm text-gray-500">Completed</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 bg-yellow-100 rounded-lg">
                        <Clock className="h-6 w-6 text-yellow-600" />
                      </div>
                      <div>
                        <p className="text-2xl font-bold text-gray-900">{inProgressWaves}</p>
                        <p className="text-sm text-gray-500">In Progress</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Wave Timeline */}
              {waves.length === 0 ? (
                <Card className="text-center py-12">
                  <CardContent>
                    <Calendar className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No Waves Defined</h3>
                    <p className="text-gray-600">Complete wave planning to see your migration roadmap.</p>
                  </CardContent>
                </Card>
              ) : (
                <div className="space-y-6">
                  {waves.map((wave, index) => (
                    <Card key={wave.wave_id || `wave-${wave.wave_number}`} className="overflow-hidden">
                      {/* Wave Header */}
                      <CardHeader className="bg-gradient-to-r from-gray-50 to-white border-b">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-4">
                            <div className="flex items-center justify-center w-10 h-10 bg-blue-600 text-white rounded-full font-bold">
                              {wave.wave_number}
                            </div>
                            <div>
                              <CardTitle className="text-xl">{wave.wave_name}</CardTitle>
                              <p className="text-sm text-gray-500 mt-1">
                                {formatDate(wave.start_date)} - {formatDate(wave.end_date)}
                                {wave.duration_days && ` (${wave.duration_days} days)`}
                              </p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            {getRiskBadge(wave.risk_level)}
                            {getStatusBadge(wave.status)}
                          </div>
                        </div>

                        {/* Progress Bar */}
                        <div className="mt-4">
                          <div className="flex justify-between text-xs text-gray-500 mb-1">
                            <span>Progress</span>
                            <span>{getProgressPercentage(wave.status)}%</span>
                          </div>
                          <div className="w-full bg-gray-200 rounded-full h-2">
                            <div
                              className={cn(
                                'h-2 rounded-full transition-all duration-500',
                                wave.status === 'completed' ? 'bg-green-500' :
                                wave.status === 'blocked' ? 'bg-red-500' :
                                wave.status === 'in_progress' || wave.status === 'in-progress' ? 'bg-blue-500' :
                                'bg-gray-300'
                              )}
                              style={{ width: `${getProgressPercentage(wave.status)}%` }}
                            />
                          </div>
                        </div>
                      </CardHeader>

                      <CardContent className="pt-6">
                        {/* Wave Description */}
                        {wave.description && (
                          <p className="text-gray-600 mb-4">{wave.description}</p>
                        )}

                        {/* Applications */}
                        <div>
                          <h4 className="text-sm font-semibold text-gray-700 mb-3 flex items-center">
                            <AppWindow className="h-4 w-4 mr-2" />
                            Applications ({wave.application_count || wave.applications?.length || 0})
                          </h4>

                          {wave.applications && wave.applications.length > 0 ? (
                            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
                              {wave.applications.map((app: WaveApplication) => (
                                <div
                                  key={app.application_id}
                                  className="p-3 bg-gray-50 rounded-lg border border-gray-100 hover:border-blue-200 transition-colors"
                                >
                                  <p className="font-medium text-gray-900 text-sm truncate" title={app.application_name}>
                                    {app.application_name}
                                  </p>
                                  <div className="flex items-center space-x-2 mt-2">
                                    {app.migration_strategy && (
                                      <Badge variant="outline" className="text-xs">
                                        {app.migration_strategy}
                                      </Badge>
                                    )}
                                    {app.criticality && (
                                      <span className={cn(
                                        'text-xs px-1.5 py-0.5 rounded',
                                        app.criticality === 'high' ? 'bg-red-100 text-red-700' :
                                        app.criticality === 'medium' ? 'bg-yellow-100 text-yellow-700' :
                                        'bg-green-100 text-green-700'
                                      )}>
                                        {app.criticality}
                                      </span>
                                    )}
                                  </div>
                                </div>
                              ))}
                            </div>
                          ) : (
                            <p className="text-sm text-gray-500 italic">No applications assigned yet</p>
                          )}
                        </div>

                        {/* Wave Groups (if any) */}
                        {wave.groups && wave.groups.length > 0 && (
                          <div className="mt-6 pt-4 border-t">
                            <h4 className="text-sm font-semibold text-gray-700 mb-3">Migration Groups</h4>
                            <div className="flex flex-wrap gap-2">
                              {wave.groups.map((group) => (
                                <div key={group.group_id} className="px-3 py-2 bg-blue-50 rounded-lg">
                                  <p className="text-sm font-medium text-blue-900">{group.group_name}</p>
                                  <p className="text-xs text-blue-600">
                                    {group.application_count} apps
                                    {group.migration_strategy && ` - ${group.migration_strategy}`}
                                    {group.parallel_execution && ' (Parallel)'}
                                  </p>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </CardContent>

                      {/* Wave Connection Arrow */}
                      {index < waves.length - 1 && (
                        <div className="flex justify-center py-2 bg-gray-50">
                          <ArrowRight className="h-5 w-5 text-gray-400 rotate-90" />
                        </div>
                      )}
                    </Card>
                  ))}
                </div>
              )}
            </div>
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
};

export default Roadmap;
