/**
 * Timeline Planning Page
 *
 * Displays migration timeline with Gantt chart, milestones, and schedule health.
 * Uses gradient stat cards for visual consistency with other Plan pages.
 *
 * Generated by CC - Claude Code
 */

import React, { useState } from 'react';
import {
  Flag,
  Calendar,
  Loader2,
  AlertTriangle,
  Clock,
  AlertCircle,
  ChevronRight,
  BarChart3,
  TrendingUp,
  CheckCircle2,
  Target,
  Activity
} from 'lucide-react';
import { useTimeline } from '@/hooks/useTimeline';
import Sidebar from '@/components/Sidebar';
import { SidebarProvider } from '@/components/ui/sidebar';
import { Alert } from '@/components/ui/alert';
import { Card } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import ContextBreadcrumbs from '@/components/context/ContextBreadcrumbs';
import PlanNavigation from '@/components/plan/PlanNavigation';
import TimelineGantt from '@/components/plan/TimelineGantt';

/**
 * SVG Health Ring Component
 * Circular progress indicator for schedule health
 */
const HealthRing = ({ percentage, status }: { percentage: number; status: string }) => {
  const radius = 40;
  const strokeWidth = 8;
  const normalizedRadius = radius - strokeWidth / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset = circumference - (percentage / 100) * circumference;

  const getColor = () => {
    if (status === 'On Track' || status === 'Completed') return '#10b981';
    if (status === 'At Risk') return '#f59e0b';
    if (status === 'Delayed') return '#ef4444';
    return '#6b7280';
  };

  return (
    <svg width={radius * 2} height={radius * 2} className="transform -rotate-90">
      {/* Background circle */}
      <circle
        stroke="#e5e7eb"
        fill="transparent"
        strokeWidth={strokeWidth}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      {/* Progress circle */}
      <circle
        stroke={getColor()}
        fill="transparent"
        strokeWidth={strokeWidth}
        strokeLinecap="round"
        strokeDasharray={circumference + ' ' + circumference}
        style={{ strokeDashoffset }}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
        className="transition-all duration-500"
      />
    </svg>
  );
};

const Timeline = (): JSX.Element => {
  const { data, isLoading, isError, error } = useTimeline();
  const [viewMode, setViewMode] = useState<'list' | 'gantt'>('gantt');

  if (isLoading) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 flex items-center justify-center">
            <div className="flex flex-col items-center space-y-4">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <p className="text-gray-600">Loading timeline data...</p>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  if (isError) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 p-8">
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <p>Error loading timeline data: {error?.message}</p>
            </Alert>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  const getStatusColor = (status: string): string => {
    const colors: Record<string, string> = {
      'Not Started': 'bg-gray-100 text-gray-800 border-gray-200',
      'In Progress': 'bg-blue-100 text-blue-800 border-blue-200',
      'Completed': 'bg-green-100 text-green-800 border-green-200',
      'Delayed': 'bg-red-100 text-red-800 border-red-200',
      'At Risk': 'bg-yellow-100 text-yellow-800 border-yellow-200',
      'Pending': 'bg-purple-100 text-purple-800 border-purple-200',
      'On Track': 'bg-green-100 text-green-800 border-green-200'
    };
    return colors[status] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getImpactColor = (impact: string): string => {
    const colors: Record<string, string> = {
      'High': 'bg-red-100 text-red-800 border-red-200',
      'Medium': 'bg-yellow-100 text-yellow-800 border-yellow-200',
      'Low': 'bg-green-100 text-green-800 border-green-200'
    };
    return colors[impact] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getHealthGradient = (status: string): string => {
    if (status === 'On Track' || status === 'Completed') {
      return 'from-emerald-500 to-teal-500';
    }
    if (status === 'At Risk') {
      return 'from-amber-500 to-orange-500';
    }
    if (status === 'Delayed') {
      return 'from-red-500 to-rose-500';
    }
    return 'from-slate-500 to-gray-500';
  };

  // Calculate completed milestones
  const totalMilestones = data.phases.reduce((acc, phase) => acc + phase.milestones.length, 0);
  const completedMilestones = data.phases.reduce(
    (acc, phase) => acc + phase.milestones.filter(m => m.status === 'Completed').length,
    0
  );

  return (
    <SidebarProvider>
      <div className="min-h-screen bg-gray-50 flex">
        <Sidebar />
        <div className="flex-1 ml-64">
          <main className="p-8">
            {/* Context Breadcrumbs */}
            <ContextBreadcrumbs showContextSelector={true} />

            {/* Plan Navigation Tabs */}
            <PlanNavigation />

            <div className="max-w-7xl mx-auto">
              {/* Header */}
              <div className="mb-8">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">Migration Timeline</h1>
                    <p className="text-lg text-gray-600">
                      Track and manage the migration schedule and milestones
                    </p>
                  </div>
                  <div className="flex items-center space-x-3">
                    {/* View Mode Toggle */}
                    <div className="flex items-center bg-white rounded-lg border border-gray-200 p-1">
                      <Button
                        variant={viewMode === 'gantt' ? 'default' : 'ghost'}
                        size="sm"
                        onClick={() => setViewMode('gantt')}
                      >
                        <BarChart3 className="h-4 w-4 mr-2" />
                        Gantt View
                      </Button>
                      <Button
                        variant={viewMode === 'list' ? 'default' : 'ghost'}
                        size="sm"
                        onClick={() => setViewMode('list')}
                      >
                        <Calendar className="h-4 w-4 mr-2" />
                        List View
                      </Button>
                    </div>

                    <Button variant="outline" className="bg-white">
                      <Calendar className="h-5 w-5 mr-2" />
                      Export Schedule
                    </Button>
                  </div>
                </div>
              </div>

              {/* Gradient Statistics Cards */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                {/* Duration - Blue Gradient */}
                <Card className="relative overflow-hidden">
                  <div className="absolute inset-0 bg-gradient-to-br from-blue-500 to-blue-600" />
                  <div className="relative p-6 text-white">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-blue-100 text-sm font-medium">Duration</p>
                        <p className="text-3xl font-bold mt-1">{data.metrics.total_duration_weeks}</p>
                        <p className="text-blue-100 text-sm mt-1">weeks</p>
                      </div>
                      <div className="bg-white/20 rounded-full p-3">
                        <Clock className="h-8 w-8 text-white" />
                      </div>
                    </div>
                  </div>
                </Card>

                {/* Progress - Orange Gradient */}
                <Card className="relative overflow-hidden">
                  <div className="absolute inset-0 bg-gradient-to-br from-orange-400 to-orange-500" />
                  <div className="relative p-6 text-white">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-orange-100 text-sm font-medium">Progress</p>
                        <p className="text-3xl font-bold mt-1">{data.metrics.overall_progress}%</p>
                        <p className="text-orange-100 text-sm mt-1">complete</p>
                      </div>
                      <div className="bg-white/20 rounded-full p-3">
                        <TrendingUp className="h-8 w-8 text-white" />
                      </div>
                    </div>
                  </div>
                </Card>

                {/* Milestones - Purple Gradient */}
                <Card className="relative overflow-hidden">
                  <div className="absolute inset-0 bg-gradient-to-br from-violet-500 to-purple-600" />
                  <div className="relative p-6 text-white">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-violet-100 text-sm font-medium">Milestones</p>
                        <p className="text-3xl font-bold mt-1">{completedMilestones}/{totalMilestones}</p>
                        <p className="text-violet-100 text-sm mt-1">completed</p>
                      </div>
                      <div className="bg-white/20 rounded-full p-3">
                        <Flag className="h-8 w-8 text-white" />
                      </div>
                    </div>
                  </div>
                </Card>

                {/* Schedule Health - Dynamic Gradient with Ring */}
                <Card className="relative overflow-hidden">
                  <div className={`absolute inset-0 bg-gradient-to-br ${getHealthGradient(data.schedule_health.status)}`} />
                  <div className="relative p-6 text-white">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-white/80 text-sm font-medium">Schedule Health</p>
                        <p className="text-xl font-bold mt-1">{data.schedule_health.status}</p>
                        <p className="text-white/80 text-sm mt-1">{data.metrics.delayed_milestones} delays</p>
                      </div>
                      <div className="relative">
                        <HealthRing
                          percentage={data.metrics.overall_progress}
                          status={data.schedule_health.status}
                        />
                        <div className="absolute inset-0 flex items-center justify-center">
                          <Activity className="h-6 w-6 text-white" />
                        </div>
                      </div>
                    </div>
                  </div>
                </Card>
              </div>

              {/* Schedule Health Analysis */}
              <Card className="mb-8">
                <div className="p-6">
                  <h2 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                    <AlertCircle className="h-5 w-5 text-amber-500" />
                    Schedule Health Analysis
                  </h2>
                  <div className="space-y-3">
                    {data.schedule_health.issues.map((issue, index) => (
                      <div
                        key={index}
                        className="flex items-start gap-3 p-4 bg-gradient-to-r from-amber-50 to-white rounded-lg border border-amber-200"
                      >
                        <AlertTriangle className="h-5 w-5 text-amber-500 flex-shrink-0 mt-0.5" />
                        <p className="text-sm text-gray-700">{issue}</p>
                      </div>
                    ))}
                    {data.schedule_health.recommendations.map((rec, index) => (
                      <div
                        key={index}
                        className="flex items-start gap-3 p-4 bg-gradient-to-r from-blue-50 to-white rounded-lg border border-blue-200"
                      >
                        <CheckCircle2 className="h-5 w-5 text-blue-500 flex-shrink-0 mt-0.5" />
                        <p className="text-sm text-gray-700">{rec}</p>
                      </div>
                    ))}
                  </div>
                </div>
              </Card>

              {/* Critical Path */}
              <Card className="mb-8">
                <div className="p-6">
                  <h2 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                    <Target className="h-5 w-5 text-purple-500" />
                    Critical Path
                  </h2>
                  <div className="flex items-center flex-wrap gap-2">
                    {data.critical_path.map((phase, index) => (
                      <React.Fragment key={index}>
                        <Badge
                          variant="outline"
                          className="bg-gradient-to-r from-slate-50 to-white px-4 py-2 text-sm font-medium"
                        >
                          {phase}
                        </Badge>
                        {index < data.critical_path.length - 1 && (
                          <ChevronRight className="h-5 w-5 text-gray-400" />
                        )}
                      </React.Fragment>
                    ))}
                  </div>
                </div>
              </Card>

              {/* Gantt Chart View */}
              {viewMode === 'gantt' && (
                <TimelineGantt
                  data={data}
                  onPhaseUpdate={(phaseId, startDate, endDate) => {
                    console.log('Phase updated:', phaseId, startDate, endDate);
                    // TODO: Implement API call to update phase dates
                  }}
                  isLoading={isLoading}
                />
              )}

              {/* Timeline Phases List View */}
              {viewMode === 'list' && (
                <div className="space-y-6">
                  {data.phases.map((phase) => (
                    <Card key={phase.id} className="overflow-hidden">
                      <div className="bg-gradient-to-r from-slate-100 to-slate-50 px-6 py-4 border-b border-slate-200">
                        <div className="flex items-center justify-between">
                          <div>
                            <h3 className="text-lg font-semibold text-gray-900">{phase.name}</h3>
                            <p className="text-sm text-gray-600 flex items-center gap-1 mt-1">
                              <Calendar className="h-4 w-4" />
                              {new Date(phase.start_date).toLocaleDateString()} - {new Date(phase.end_date).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="flex items-center space-x-4">
                            <Badge className={`${getStatusColor(phase.status)} border`}>
                              {phase.status}
                            </Badge>
                            <div className="w-32">
                              <div className="flex justify-between text-xs text-gray-500 mb-1">
                                <span>Progress</span>
                                <span className="font-medium">{phase.progress}%</span>
                              </div>
                              <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
                                <div
                                  className="h-full bg-blue-500 transition-all"
                                  style={{ width: `${phase.progress}%` }}
                                />
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="p-6 space-y-4">
                        {/* Dependencies */}
                        {phase.dependencies.length > 0 && (
                          <div>
                            <p className="text-sm font-medium text-gray-600 mb-2">Dependencies</p>
                            <div className="flex flex-wrap gap-2">
                              {phase.dependencies.map((dep, index) => (
                                <Badge key={index} variant="outline" className="bg-slate-50">
                                  {dep}
                                </Badge>
                              ))}
                            </div>
                          </div>
                        )}

                        {/* Milestones */}
                        <div>
                          <p className="text-sm font-medium text-gray-600 mb-3">Milestones</p>
                          <div className="space-y-2">
                            {phase.milestones.map((milestone, index) => (
                              <div
                                key={index}
                                className="flex items-center justify-between bg-gradient-to-r from-slate-50 to-white p-4 rounded-lg border border-slate-200"
                              >
                                <div className="flex items-start gap-3">
                                  <Flag className={`h-5 w-5 mt-0.5 ${
                                    milestone.status === 'Completed' ? 'text-green-500' :
                                    milestone.status === 'Delayed' ? 'text-red-500' :
                                    'text-gray-400'
                                  }`} />
                                  <div>
                                    <div className="font-medium text-gray-900">{milestone.name}</div>
                                    <div className="text-sm text-gray-600 mt-0.5">{milestone.description}</div>
                                    <div className="text-xs text-gray-500 mt-1 flex items-center gap-1">
                                      <Calendar className="h-3 w-3" />
                                      {new Date(milestone.date).toLocaleDateString()}
                                    </div>
                                  </div>
                                </div>
                                <Badge className={`${getStatusColor(milestone.status)} border`}>
                                  {milestone.status}
                                </Badge>
                              </div>
                            ))}
                          </div>
                        </div>

                        {/* Risks */}
                        {phase.risks.length > 0 && (
                          <div>
                            <p className="text-sm font-medium text-gray-600 mb-3">Risks</p>
                            <div className="space-y-2">
                              {phase.risks.map((risk, index) => (
                                <div
                                  key={index}
                                  className="flex items-center justify-between bg-gradient-to-r from-red-50 to-white p-4 rounded-lg border border-red-200"
                                >
                                  <div className="flex items-start gap-3">
                                    <AlertTriangle className="h-5 w-5 text-red-500 flex-shrink-0 mt-0.5" />
                                    <div>
                                      <div className="text-sm text-gray-900">{risk.description}</div>
                                      <div className="text-sm text-gray-600 mt-1">
                                        <span className="font-medium">Mitigation:</span> {risk.mitigation}
                                      </div>
                                    </div>
                                  </div>
                                  <Badge className={`${getImpactColor(risk.impact)} border`}>
                                    {risk.impact} Impact
                                  </Badge>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </Card>
                  ))}
                </div>
              )}
            </div>
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
};

export default Timeline;
