/**
 * Wave Planning Page
 *
 * Main page for wave planning functionality. Displays wave dashboard
 * and provides interface for creating/editing migration waves.
 *
 * Uses React Router with query parameters for engagement_id and planning_flow_id.
 * All field names use snake_case per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { planningFlowApi } from '@/lib/api/planningFlowService';
import type { Wave } from '@/lib/api/planningFlowService';
import WaveDashboard from '@/components/plan/WaveDashboard';
import WaveModal from '@/components/plan/WaveModal';
import { Sidebar } from '@/components/ui/sidebar';
import { SidebarProvider } from '@/components/ui/sidebar';
import { useToast } from '@/hooks/use-toast';

export default function WavePlanningPage(): JSX.Element {
  const [searchParams] = useSearchParams();
  const { toast } = useToast();

  // Get query parameters (snake_case per CLAUDE.md)
  const engagement_id = searchParams.get('engagement_id');
  const planning_flow_id = searchParams.get('planning_flow_id');

  // State management
  const [waves, setWaves] = useState<Wave[]>([]);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedWave, setSelectedWave] = useState<Wave | null>(null);
  const [error, setError] = useState<string | null>(null);

  // Load planning data on mount
  useEffect(() => {
    if (planning_flow_id) {
      loadPlanningData();
    } else {
      setLoading(false);
      setError('No planning flow ID provided');
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [planning_flow_id]);

  /**
   * Load wave planning data from API.
   * Uses snake_case field names throughout.
   */
  const loadPlanningData = async () => {
    if (!planning_flow_id) return;

    try {
      setLoading(true);
      setError(null);

      const status = await planningFlowApi.getPlanningStatus(planning_flow_id);

      // Extract waves from wave_plan_data (snake_case!)
      const wavesData = status.wave_plan_data?.waves || [];
      setWaves(wavesData);
    } catch (error) {
      console.error('Failed to load planning data:', error);
      setError(error instanceof Error ? error.message : 'Failed to load planning data');

      toast({
        title: 'Error Loading Planning Data',
        description: error instanceof Error ? error.message : 'Unable to load wave planning data',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  /**
   * Handle creating a new wave.
   * Opens modal with empty form.
   */
  const handleCreateWave = () => {
    setSelectedWave(null);
    setIsModalOpen(true);
  };

  /**
   * Handle editing an existing wave.
   * Opens modal with pre-filled form.
   */
  const handleEditWave = (wave: Wave) => {
    setSelectedWave(wave);
    setIsModalOpen(true);
  };

  /**
   * Handle saving wave (create or update).
   * Uses snake_case field names in API call.
   */
  const handleSaveWave = async (waveData: Partial<Wave>) => {
    if (!planning_flow_id) {
      toast({
        title: 'Error',
        description: 'No planning flow ID available',
        variant: 'destructive',
      });
      return;
    }

    try {
      // Update wave plan data with new/modified wave
      const updatedWaves = selectedWave
        ? waves.map((w) =>
            w.wave_number === selectedWave.wave_number
              ? { ...w, ...waveData }
              : w
          )
        : [
            ...waves,
            {
              ...waveData,
              wave_number: waves.length + 1,
              applications: waveData.applications || [],
              status: waveData.status || 'planned',
            } as Wave,
          ];

      // Call API with snake_case fields
      await planningFlowApi.updateWavePlan(planning_flow_id, {
        waves: updatedWaves,
        total_waves: updatedWaves.length,
      });

      setWaves(updatedWaves);
      setIsModalOpen(false);

      toast({
        title: 'Wave Saved',
        description: `Wave "${waveData.wave_name}" has been saved successfully`,
      });
    } catch (error) {
      console.error('Failed to save wave:', error);
      toast({
        title: 'Error Saving Wave',
        description: error instanceof Error ? error.message : 'Unable to save wave',
        variant: 'destructive',
      });
    }
  };

  /**
   * Handle deleting a wave.
   * Removes wave from plan and renumbers remaining waves.
   */
  const handleDeleteWave = async (wave: Wave) => {
    if (!planning_flow_id) return;

    // Confirm deletion
    if (!window.confirm(`Are you sure you want to delete "${wave.wave_name}"?`)) {
      return;
    }

    try {
      // Remove wave and renumber
      const updatedWaves = waves
        .filter((w) => w.wave_number !== wave.wave_number)
        .map((w, index) => ({
          ...w,
          wave_number: index + 1, // Renumber from 1
        }));

      await planningFlowApi.updateWavePlan(planning_flow_id, {
        waves: updatedWaves,
        total_waves: updatedWaves.length,
      });

      setWaves(updatedWaves);

      toast({
        title: 'Wave Deleted',
        description: `Wave "${wave.wave_name}" has been deleted`,
      });
    } catch (error) {
      console.error('Failed to delete wave:', error);
      toast({
        title: 'Error Deleting Wave',
        description: error instanceof Error ? error.message : 'Unable to delete wave',
        variant: 'destructive',
      });
    }
  };

  // Loading state
  if (loading) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 flex items-center justify-center">
            <div className="text-center">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mb-4"></div>
              <p className="text-gray-600">Loading wave planning data...</p>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  // Error state
  if (error) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 p-8">
            <div className="bg-red-50 border border-red-200 rounded-lg p-6">
              <h2 className="text-xl font-semibold text-red-800 mb-2">
                Error Loading Planning Data
              </h2>
              <p className="text-red-700">{error}</p>
              <button
                onClick={loadPlanningData}
                className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Retry
              </button>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  return (
    <SidebarProvider>
      <div className="min-h-screen bg-gray-50 flex">
        <Sidebar />
        <div className="flex-1 ml-64 p-8">
          {/* Header */}
          <div className="mb-6">
            <h1 className="text-3xl font-bold text-gray-900">Wave Planning</h1>
            <p className="text-gray-600 mt-2">
              Organize applications into migration waves for phased execution
            </p>
          </div>

          {/* Actions */}
          <div className="mb-6 flex gap-3">
            <button
              onClick={handleCreateWave}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors font-medium"
            >
              + Create Wave
            </button>
            <button
              onClick={loadPlanningData}
              className="bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors font-medium"
            >
              Refresh
            </button>
          </div>

          {/* Wave Dashboard */}
          <WaveDashboard
            waves={waves}
            onEditWave={handleEditWave}
            onDeleteWave={handleDeleteWave}
          />

          {/* Wave Modal */}
          {isModalOpen && (
            <WaveModal
              wave={selectedWave}
              onSave={handleSaveWave}
              onClose={() => setIsModalOpen(false)}
            />
          )}
        </div>
      </div>
    </SidebarProvider>
  );
}
