/**
 * Wave Planning Page
 *
 * Main page for wave planning functionality. Displays wave dashboard
 * and provides interface for creating/editing migration waves.
 *
 * Uses React Router with query parameters for engagement_id and planning_flow_id.
 * All field names use snake_case per CLAUDE.md.
 *
 * Generated by CC - Claude Code
 */

import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { planningFlowApi } from '@/lib/api/planningFlowService';
import type { Wave } from '@/lib/api/planningFlowService';
import WaveDashboard from '@/components/plan/WaveDashboard';
import WaveModal from '@/components/plan/WaveModal';
import Sidebar from '@/components/Sidebar';
import { SidebarProvider } from '@/components/ui/sidebar';
import { useToast } from '@/hooks/use-toast';
import ContextBreadcrumbs from '@/components/context/ContextBreadcrumbs';
import PlanNavigation from '@/components/plan/PlanNavigation';
import { apiCall } from '@/config/api';
import {
  Loader2,
  AlertCircle,
  Calendar,
  ChevronRight,
  Layers,
  AppWindow,
  ClipboardList,
  CheckCircle2,
  Plus,
  RefreshCw
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';

// Interface for planning flow list item
interface PlanningFlowListItem {
  planning_flow_id: string;
  current_phase: string;
  phase_status: string;
  created_at: string;
  wave_count?: number;
  application_count?: number;
}

export default function WavePlanningPage(): JSX.Element {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { toast } = useToast();

  // Get query parameters (snake_case per CLAUDE.md)
  const engagement_id = searchParams.get('engagement_id');
  const planning_flow_id = searchParams.get('planning_flow_id');

  // State management
  const [waves, setWaves] = useState<Wave[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedWave, setSelectedWave] = useState<Wave | null>(null);
  const [applicationsMap, setApplicationsMap] = useState<Map<string, { id: string; name: string }>>(new Map());

  // Fetch available planning flows if no flow_id provided
  const {
    data: planningFlows,
    isLoading: isLoadingFlows,
    error: flowsError
  } = useQuery<PlanningFlowListItem[]>({
    queryKey: ['planning-flows'],
    queryFn: async () => {
      // Call wave planning endpoint to get available flows
      const response = await apiCall('/api/v1/wave-planning/');
      // If planning_status is "not_started", return empty array
      if (response.planning_status === 'not_started') {
        return [];
      }
      // Otherwise return as array (backend returns single flow for now)
      return response.planning_flow_id ? [{
        planning_flow_id: response.planning_flow_id,
        current_phase: response.planning_status,
        phase_status: response.phase_status,
        created_at: new Date().toISOString(),
        wave_count: response.waves?.length || 0,
        application_count: response.summary?.total_apps || 0
      }] : [];
    },
    enabled: !planning_flow_id, // Only fetch if no flow_id in URL
    staleTime: 30000
  });

  // Fetch planning data when flow_id is available
  const {
    data: planningStatus,
    isLoading: isLoadingPlanningData,
    error: planningError,
    refetch: refetchPlanningData
  } = useQuery({
    queryKey: ['planning-status', planning_flow_id],
    queryFn: () => planningFlowApi.getPlanningStatus(planning_flow_id),
    enabled: !!planning_flow_id,
    staleTime: 5000,
    refetchInterval: (data) => {
      // Poll more frequently if planning is in progress
      if (!data) return false;
      if (data.phase_status === 'completed') return false;
      return data.phase_status === 'in_progress' ? 5000 : 15000;
    }
  });

  // Update waves when planning data changes
  useEffect(() => {
    if (planningStatus?.wave_plan_data?.waves) {
      setWaves(planningStatus.wave_plan_data.waves);

      // Build applications map from all waves
      // Backend returns objects with application_id, application_name, migration_strategy, etc.
      // The map is used as a fallback for legacy string-based application arrays
      const appMap = new Map<string, { id: string; name: string }>();
      planningStatus.wave_plan_data.waves.forEach((wave) => {
        wave.applications?.forEach((app: unknown) => {
          // Handle new object format from backend (with application_id)
          if (typeof app === 'object' && app !== null && 'application_id' in app) {
            const appObj = app as { application_id: string; application_name?: string };
            if (!appMap.has(appObj.application_id)) {
              appMap.set(appObj.application_id, {
                id: appObj.application_id,
                name: appObj.application_name || `App ${appObj.application_id.substring(0, 8)}...`,
              });
            }
          }
          // Handle legacy string format (UUID only)
          else if (typeof app === 'string') {
            if (!appMap.has(app)) {
              appMap.set(app, {
                id: app,
                name: `Application ${app.substring(0, 8)}...`,
              });
            }
          }
        });
      });
      setApplicationsMap(appMap);
    }
  }, [planningStatus]);

  // Calculate statistics
  const totalWaves = waves.length;
  const totalApplications = waves.reduce((sum, wave) => sum + (wave.applications?.length || 0), 0);
  const plannedWaves = waves.filter(w => w.status === 'planned' || w.status === 'in_progress').length;
  const completedWaves = waves.filter(w => w.status === 'completed').length;

  /**
   * Handle selecting a planning flow from the list
   */
  const handleSelectFlow = (flow: PlanningFlowListItem) => {
    navigate(`/plan/waveplanning?planning_flow_id=${flow.planning_flow_id}&engagement_id=${engagement_id || 1}`);
  };

  /**
   * Handle creating a new wave.
   * Opens modal with empty form.
   */
  const handleCreateWave = () => {
    setSelectedWave(null);
    setIsModalOpen(true);
  };

  /**
   * Handle editing an existing wave.
   * Opens modal with pre-filled form.
   */
  const handleEditWave = (wave: Wave) => {
    setSelectedWave(wave);
    setIsModalOpen(true);
  };

  /**
   * Handle saving wave (create or update).
   * Uses snake_case field names in API call.
   */
  const handleSaveWave = async (waveData: Partial<Wave>) => {
    if (!planning_flow_id) {
      toast({
        title: 'Error',
        description: 'No planning flow ID available',
        variant: 'destructive',
      });
      return;
    }

    try {
      // Update wave plan data with new/modified wave
      const updatedWaves = selectedWave
        ? waves.map((w) =>
            w.wave_number === selectedWave.wave_number
              ? { ...w, ...waveData }
              : w
          )
        : [
            ...waves,
            {
              ...waveData,
              wave_number: waves.length + 1,
              applications: waveData.applications || [],
              status: waveData.status || 'planned',
            } as Wave,
          ];

      // Call API with snake_case fields
      await planningFlowApi.updateWavePlan(planning_flow_id, {
        waves: updatedWaves,
        total_waves: updatedWaves.length,
      });

      setWaves(updatedWaves);
      setIsModalOpen(false);
      refetchPlanningData(); // Refresh data after save

      toast({
        title: 'Wave Saved',
        description: `Wave "${waveData.wave_name}" has been saved successfully`,
      });
    } catch (error) {
      console.error('Failed to save wave:', error);
      toast({
        title: 'Error Saving Wave',
        description: error instanceof Error ? error.message : 'Unable to save wave',
        variant: 'destructive',
      });
    }
  };

  /**
   * Handle moving an application between waves.
   * Updates wave assignments and persists via API.
   */
  const handleApplicationMoved = async (
    appId: string,
    fromWave: number,
    toWave: number
  ) => {
    if (!planning_flow_id) return;

    try {
      // Update waves locally (optimistic update)
      const updatedWaves = waves.map((wave) => {
        // Remove from source wave
        if (wave.wave_number === fromWave) {
          return {
            ...wave,
            applications: (wave.applications || []).filter((id) => id !== appId),
            application_count: Math.max(0, (wave.application_count || 0) - 1),
          };
        }
        // Add to target wave
        if (wave.wave_number === toWave) {
          return {
            ...wave,
            applications: [...(wave.applications || []), appId],
            application_count: (wave.application_count || 0) + 1,
          };
        }
        return wave;
      });

      // Persist to backend
      await planningFlowApi.updateWavePlan(planning_flow_id, {
        waves: updatedWaves,
        total_waves: updatedWaves.length,
      });

      // Refresh data from server to ensure consistency (avoid redundant local state update)
      refetchPlanningData();

      toast({
        title: 'Application Moved',
        description: `Application moved from Wave ${fromWave} to Wave ${toWave}`,
      });
    } catch (error) {
      console.error('Failed to move application:', error);
      toast({
        title: 'Error Moving Application',
        description: error instanceof Error ? error.message : 'Unable to move application',
        variant: 'destructive',
      });
      // Revert local state by refetching
      refetchPlanningData();
    }
  };

  /**
   * Handle deleting a wave.
   * Removes wave from plan and renumbers remaining waves.
   */
  const handleDeleteWave = async (wave: Wave) => {
    if (!planning_flow_id) return;

    // Confirm deletion
    if (!window.confirm(`Are you sure you want to delete "${wave.wave_name}"?`)) {
      return;
    }

    try {
      // Remove wave and renumber
      const updatedWaves = waves
        .filter((w) => w.wave_number !== wave.wave_number)
        .map((w, index) => ({
          ...w,
          wave_number: index + 1, // Renumber from 1
        }));

      await planningFlowApi.updateWavePlan(planning_flow_id, {
        waves: updatedWaves,
        total_waves: updatedWaves.length,
      });

      setWaves(updatedWaves);
      refetchPlanningData(); // Refresh data after delete

      toast({
        title: 'Wave Deleted',
        description: `Wave "${wave.wave_name}" has been deleted`,
      });
    } catch (error) {
      console.error('Failed to delete wave:', error);
      toast({
        title: 'Error Deleting Wave',
        description: error instanceof Error ? error.message : 'Unable to delete wave',
        variant: 'destructive',
      });
    }
  };

  // Show flow selection if no flow_id provided
  if (!planning_flow_id) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 p-8">
            <ContextBreadcrumbs showContextSelector={true} />

            <div className="mb-6">
              <h1 className="text-3xl font-bold text-gray-900">Wave Planning</h1>
              <p className="text-gray-600 mt-2">
                Select a planning flow or create a new one
              </p>
            </div>

            {isLoadingFlows ? (
              <div className="flex items-center justify-center py-12">
                <Loader2 className="h-8 w-8 animate-spin text-blue-600 mr-3" />
                <p className="text-gray-600">Loading planning flows...</p>
              </div>
            ) : flowsError ? (
              <div className="bg-red-50 border border-red-200 rounded-lg p-6">
                <div className="flex items-start space-x-3">
                  <AlertCircle className="h-6 w-6 text-red-600 mt-0.5" />
                  <div>
                    <h3 className="text-lg font-semibold text-red-800 mb-1">Error Loading Flows</h3>
                    <p className="text-red-700">
                      {flowsError instanceof Error ? flowsError.message : 'Failed to load planning flows'}
                    </p>
                  </div>
                </div>
              </div>
            ) : planningFlows && planningFlows.length > 0 ? (
              <div className="space-y-4">
                <h2 className="text-lg font-semibold text-gray-900">Available Planning Flows</h2>
                {planningFlows.map((flow) => (
                  <Card key={flow.planning_flow_id} className="p-6 hover:shadow-lg transition-shadow cursor-pointer"
                        onClick={() => handleSelectFlow(flow)}>
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <h3 className="font-semibold text-gray-900 mb-2">
                          Planning Flow
                        </h3>
                        <div className="grid grid-cols-2 gap-4 text-sm">
                          <div>
                            <span className="text-gray-500">Phase:</span>
                            <span className="ml-2 font-medium">{flow.current_phase}</span>
                          </div>
                          <div>
                            <span className="text-gray-500">Status:</span>
                            <span className="ml-2 font-medium">{flow.phase_status}</span>
                          </div>
                          <div>
                            <span className="text-gray-500">Waves:</span>
                            <span className="ml-2 font-medium">{flow.wave_count || 0}</span>
                          </div>
                          <div>
                            <span className="text-gray-500">Applications:</span>
                            <span className="ml-2 font-medium">{flow.application_count || 0}</span>
                          </div>
                        </div>
                      </div>
                      <ChevronRight className="h-6 w-6 text-gray-400" />
                    </div>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-8 text-center">
                <Calendar className="h-12 w-12 text-blue-600 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-blue-900 mb-2">
                  No Planning Flows Found
                </h3>
                <p className="text-blue-800 mb-4">
                  Start by creating a new planning flow from the Plan Overview page
                </p>
                <Button onClick={() => navigate('/plan')}>
                  Go to Plan Overview
                </Button>
              </div>
            )}
          </div>
        </div>
      </SidebarProvider>
    );
  }

  // Loading state for specific flow
  if (isLoadingPlanningData) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 flex items-center justify-center">
            <div className="text-center">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600 mb-4 mx-auto" />
              <p className="text-gray-600">Loading wave planning data...</p>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  // Error state for specific flow
  if (planningError) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64 p-8">
            <div className="bg-red-50 border border-red-200 rounded-lg p-6">
              <div className="flex items-start space-x-3">
                <AlertCircle className="h-6 w-6 text-red-600 mt-0.5" />
                <div className="flex-1">
                  <h2 className="text-xl font-semibold text-red-800 mb-2">
                    Error Loading Planning Data
                  </h2>
                  <p className="text-red-700 mb-4">
                    {planningError instanceof Error ? planningError.message : 'Failed to load planning data'}
                  </p>
                  <Button onClick={() => refetchPlanningData()} variant="outline">
                    Retry
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  // Wave planning in progress state - AI agent is generating the wave plan
  const isWavePlanningInProgress = planningStatus?.phase_status === 'in_progress' && waves.length === 0;

  if (isWavePlanningInProgress) {
    return (
      <SidebarProvider>
        <div className="min-h-screen bg-gray-50 flex">
          <Sidebar />
          <div className="flex-1 ml-64">
            <main className="p-8">
              <ContextBreadcrumbs showContextSelector={true} />
              <PlanNavigation />

              <div className="mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Wave Planning</h1>
                <p className="text-lg text-gray-600">
                  Organize applications into migration waves for phased execution
                </p>
              </div>

              {/* In-progress indicator */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-8">
                <div className="flex flex-col items-center text-center">
                  <div className="relative mb-6">
                    <Loader2 className="h-16 w-16 animate-spin text-blue-600" />
                    <div className="absolute inset-0 flex items-center justify-center">
                      <Layers className="h-8 w-8 text-blue-400" />
                    </div>
                  </div>
                  <h2 className="text-xl font-semibold text-blue-900 mb-2">
                    AI Agent is Planning Your Waves
                  </h2>
                  <p className="text-blue-800 mb-4 max-w-lg">
                    The wave planning specialist is analyzing your {planningStatus?.summary?.total_apps || 'selected'} applications,
                    evaluating dependencies, and creating an optimal migration wave plan.
                    This typically takes 2-3 minutes.
                  </p>
                  <div className="flex items-center space-x-2 text-sm text-blue-700">
                    <RefreshCw className="h-4 w-4 animate-spin" />
                    <span>Auto-refreshing every 5 seconds...</span>
                  </div>
                  <p className="text-xs text-blue-600 mt-4">
                    Planning Flow ID: {planning_flow_id?.substring(0, 8)}...
                  </p>
                </div>
              </div>
            </main>
          </div>
        </div>
      </SidebarProvider>
    );
  }

  return (
    <SidebarProvider>
      <div className="min-h-screen bg-gray-50 flex">
        <Sidebar />
        <div className="flex-1 ml-64">
          <main className="p-8">
            {/* Context Breadcrumbs */}
            <ContextBreadcrumbs showContextSelector={true} />

            {/* Plan Navigation Tabs */}
            <PlanNavigation />

            {/* Header */}
            <div className="mb-8">
              <div className="flex items-center justify-between">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900 mb-2">Wave Planning</h1>
                  <p className="text-lg text-gray-600">
                    Organize applications into migration waves for phased execution
                  </p>
                </div>
                <div className="flex space-x-3">
                  <Button
                    onClick={() => refetchPlanningData()}
                    variant="outline"
                    className="bg-white"
                  >
                    <RefreshCw className="h-5 w-5 mr-2" />
                    Refresh
                  </Button>
                  <Button onClick={handleCreateWave}>
                    <Plus className="h-5 w-5 mr-2" />
                    Create Wave
                  </Button>
                </div>
              </div>
            </div>

            {/* Gradient Statistics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
              {/* Total Waves - Blue Gradient */}
              <Card className="relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-blue-500 to-blue-600" />
                <div className="relative p-6 text-white">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-blue-100 text-sm font-medium">Total Waves</p>
                      <p className="text-3xl font-bold mt-1">{totalWaves}</p>
                      <p className="text-blue-100 text-sm mt-1">configured</p>
                    </div>
                    <div className="bg-white/20 rounded-full p-3">
                      <Layers className="h-8 w-8 text-white" />
                    </div>
                  </div>
                </div>
              </Card>

              {/* Applications - Orange Gradient */}
              <Card className="relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-orange-400 to-orange-500" />
                <div className="relative p-6 text-white">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-orange-100 text-sm font-medium">Applications</p>
                      <p className="text-3xl font-bold mt-1">{totalApplications}</p>
                      <p className="text-orange-100 text-sm mt-1">assigned</p>
                    </div>
                    <div className="bg-white/20 rounded-full p-3">
                      <AppWindow className="h-8 w-8 text-white" />
                    </div>
                  </div>
                </div>
              </Card>

              {/* Planned - Green Gradient */}
              <Card className="relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-emerald-500 to-emerald-600" />
                <div className="relative p-6 text-white">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-emerald-100 text-sm font-medium">Planned</p>
                      <p className="text-3xl font-bold mt-1">{plannedWaves}</p>
                      <p className="text-emerald-100 text-sm mt-1">waves</p>
                    </div>
                    <div className="bg-white/20 rounded-full p-3">
                      <ClipboardList className="h-8 w-8 text-white" />
                    </div>
                  </div>
                </div>
              </Card>

              {/* Completed - Gray Gradient */}
              <Card className="relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-gray-500 to-gray-600" />
                <div className="relative p-6 text-white">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-gray-200 text-sm font-medium">Completed</p>
                      <p className="text-3xl font-bold mt-1">{completedWaves}</p>
                      <p className="text-gray-200 text-sm mt-1">waves</p>
                    </div>
                    <div className="bg-white/20 rounded-full p-3">
                      <CheckCircle2 className="h-8 w-8 text-white" />
                    </div>
                  </div>
                </div>
              </Card>
            </div>

            {/* Wave Dashboard */}
            <WaveDashboard
              waves={waves}
              applications={applicationsMap}
              onEditWave={handleEditWave}
              onDeleteWave={handleDeleteWave}
              onApplicationMoved={handleApplicationMoved}
              isDragEnabled={true}
            />

            {/* Wave Modal */}
            {isModalOpen && (
              <WaveModal
                wave={selectedWave}
                onSave={handleSaveWave}
                onClose={() => setIsModalOpen(false)}
              />
            )}
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
}
