// Communication and Notification
export interface CommunicationPlan {
  stakeholders: Stakeholder[];
  channels: CommunicationChannel[];
  templates: CommunicationTemplate[];
  schedules: CommunicationSchedule[];
  escalation: CommunicationEscalation[];
}

export interface Stakeholder {
  id: string;
  name: string;
  role: string;
  type: 'internal' | 'external';
  contactInfo: ContactInfo;
  preferences: CommunicationPreferences;
}

export interface ContactInfo {
  email?: string;
  phone?: string;
  slack?: string;
  teams?: string;
  webhook?: string;
}

export interface CommunicationPreferences {
  channels: string[];
  frequency: string;
  urgency: string[];
  timezone: string;
  quietHours?: QuietHours;
}

export interface QuietHours {
  enabled: boolean;
  start: string;
  end: string;
  timezone: string;
  exceptions: string[];
}

export interface CommunicationChannel {
  type: 'email' | 'slack' | 'webhook' | 'sms' | 'phone';
  configuration: Record<string, any>;
  rateLimiting: RateLimiting;
  failover: ChannelFailover[];
}

export interface RateLimiting {
  enabled: boolean;
  maxMessages: number;
  timeWindow: string;
  action: 'queue' | 'drop' | 'failover';
}

export interface ChannelFailover {
  condition: string;
  channel: string;
  delay: string;
}

export interface CommunicationTemplate {
  id: string;
  name: string;
  type: 'start' | 'progress' | 'completion' | 'error' | 'rollback';
  subject: string;
  body: string;
  variables: TemplateVariable[];
}

export interface TemplateVariable {
  name: string;
  type: 'string' | 'number' | 'date' | 'object';
  required: boolean;
  default?: unknown;
}

export interface CommunicationSchedule {
  event: string;
  delay: string;
  recipients: string[];
  template: string;
  channel: string[];
  conditions: string[];
}

export interface CommunicationEscalation {
  trigger: string;
  delay: string;
  recipients: string[];
  template: string;
  channels: string[];
}
