// Deployment and Operational Types
export interface LoggingDeploymentPlan {
  phases: DeploymentPhase[];
  dependencies: string[];
  validations: ValidationCheck[];
  rollback: RollbackStrategy;
  monitoring: DeploymentMonitoring;
}

export interface DeploymentPhase {
  id: string;
  name: string;
  description: string;
  tasks: DeploymentTask[];
  duration: string;
  prerequisites: string[];
  rollbackTriggers: string[];
}

export interface DeploymentTask {
  id: string;
  name: string;
  type: 'configure' | 'deploy' | 'validate' | 'test';
  description: string;
  commands: string[];
  validation: string[];
  timeout: string;
}

export interface ValidationCheck {
  id: string;
  name: string;
  type: 'connectivity' | 'configuration' | 'performance' | 'security';
  criteria: string[];
  timeout: string;
  critical: boolean;
}

export interface RollbackStrategy {
  enabled: boolean;
  triggers: string[];
  procedures: string[];
  validation: string[];
  timeout: string;
}

export interface DeploymentMonitoring {
  enabled: boolean;
  metrics: string[];
  alerts: string[];
  dashboards: string[];
  duration: string;
}

// Cost and Volume Estimation
export interface LogVolumeEstimate {
  dailyVolume: number;
  monthlyVolume: number;
  peakVolumePerHour: number;
  averageEventSize: number;
  compressionRatio: number;
  storageRequirements: StorageRequirements;
}

export interface StorageRequirements {
  hot: StorageRequirement;
  warm: StorageRequirement;
  cold: StorageRequirement;
  archive: StorageRequirement;
  total: StorageRequirement;
}

export interface StorageRequirement {
  size: string;
  iops?: number;
  throughput?: string;
  cost: CostEstimate;
}

export interface CostEstimate {
  monthly: number;
  yearly: number;
  currency: string;
  breakdown: CostBreakdown[];
}

export interface CostBreakdown {
  component: string;
  cost: number;
  unit: string;
  description: string;
}

export interface LoggingCost {
  ingestion: number;
  storage: number;
  processing: number;
  transfer: number;
  total: number;
  currency: string;
  breakdown: CostBreakdown[];
}
