// Scenario and Simulation Support Types
export interface RiskScenario {
  id: string;
  name: string;
  description: string;
  type: 'best_case' | 'worst_case' | 'most_likely' | 'stress' | 'custom';
  probability: number;
  assumptions: ScenarioAssumption[];
  variables: ScenarioVariable[];
  constraints: ScenarioConstraint[];
  outcomes: ScenarioOutcome[];
}

export interface SimulationConfig {
  engine: 'monte_carlo' | 'latin_hypercube' | 'sobol' | 'halton';
  iterations: number;
  seed: number;
  convergence: ConvergenceCriteria;
  sampling: SamplingMethod;
  validation: ValidationConfig;
  output: OutputConfig;
}

export interface RiskSimulationResult {
  summary: SimulationSummary;
  scenarios: ScenarioResult[];
  distributions: ResultDistribution[];
  statistics: ResultStatistics;
  confidence: ConfidenceLevel[];
  sensitivity: SensitivityResult[];
  recommendations: SimulationRecommendation[];
}

export interface ScenarioResult {
  scenarioId: string;
  probability: number;
  impact: ScenarioImpact;
  outcomes: ResultOutcome[];
  metrics: ScenarioMetric[];
  recommendations: string[];
}

