// Approval Management Types
export interface ApprovalRequirement {
  id: string;
  type: ApprovalType;
  name: string;
  description: string;
  approver: Approver;
  criteria: ApprovalCriteria[];
  status: ApprovalStatus;
  requestedAt?: string;
  respondedAt?: string;
  comments?: string;
  conditions?: string[];
}

export type ApprovalType =
  | 'plan' | 'budget' | 'technical' | 'business' | 'compliance' | 'security' | 'go_live';

export interface Approver {
  id: string;
  name: string;
  role: string;
  authority: ApprovalAuthority;
  delegation: ApprovalDelegation;
}

export type ApprovalAuthority = 'full' | 'conditional' | 'advisory' | 'delegated';

export interface ApprovalDelegation {
  enabled: boolean;
  delegate?: string;
  conditions?: string[];
  timeframe?: string;
}

export interface ApprovalCriteria {
  criterion: string;
  mandatory: boolean;
  validationMethod: 'automatic' | 'manual' | 'hybrid';
  evidence: string[];
}

export type ApprovalStatus =
  | 'not_requested' | 'pending' | 'under_review' | 'approved' | 'rejected'
  | 'conditional' | 'expired' | 'withdrawn';
