// Risk Assessment Base Types
export interface RiskAssessment {
  id: string;
  overallRiskLevel: RiskLevel;
  riskCategories: RiskCategory[];
  mitigationStrategies: MitigationStrategy[];
  contingencyPlans: ContingencyPlan[];
  riskMatrix: RiskMatrix;
  assessedAt: string;
  assessedBy: string;
  nextReviewDate: string;
}

export type RiskLevel = 'very_low' | 'low' | 'medium' | 'high' | 'very_high' | 'critical';

export interface RiskCategory {
  category: 'technical' | 'business' | 'operational' | 'financial' | 'compliance' | 'security';
  risks: Risk[];
  overallLevel: RiskLevel;
  impact: ImpactAssessment;
}

export interface Risk {
  id: string;
  name: string;
  description: string;
  category: string;
  likelihood: LikelihoodLevel;
  impact: ImpactLevel;
  riskLevel: RiskLevel;
  owner: string;
  status: RiskStatus;
  mitigation: string;
  contingency: string;
  identifiedAt: string;
  lastReviewedAt: string;
}

export type LikelihoodLevel = 'very_low' | 'low' | 'medium' | 'high' | 'very_high';
export type ImpactLevel = 'very_low' | 'low' | 'medium' | 'high' | 'very_high';
export type RiskStatus = 'open' | 'mitigated' | 'accepted' | 'transferred' | 'closed';

export interface MitigationStrategy {
  id: string;
  riskId: string;
  strategy: string;
  description: string;
  cost: number;
  timeline: string;
  effectiveness: number;
  owner: string;
  status: 'planned' | 'implementing' | 'implemented' | 'failed';
}

export interface ContingencyPlan {
  id: string;
  trigger: string;
  actions: ContingencyAction[];
  resources: string[];
  timeline: string;
  owner: string;
  activated: boolean;
}

export interface ContingencyAction {
  id: string;
  description: string;
  type: 'automatic' | 'manual';
  priority: number;
  duration: string;
  dependencies: string[];
}

// Risk Matrix and Analysis
export interface RiskMatrix {
  dimensions: MatrixDimension[];
  cells: MatrixCell[];
  thresholds: RiskThreshold[];
}

export interface MatrixDimension {
  name: string;
  axis: 'x' | 'y';
  scale: ScalePoint[];
}

export interface ScalePoint {
  value: number;
  label: string;
  description: string;
}

export interface MatrixCell {
  x: number;
  y: number;
  riskLevel: RiskLevel;
  color: string;
  actionRequired: boolean;
}

export interface RiskThreshold {
  level: RiskLevel;
  escalation: string[];
  approvalRequired: boolean;
  actions: string[];
}

// Impact Assessment Types
export interface ImpactAssessment {
  business: BusinessImpact;
  technical: TechnicalImpact;
  financial: FinancialImpact;
  operational: OperationalImpact;
  compliance: ComplianceImpact;
}

export interface BusinessImpact {
  level: ImpactLevel;
  description: string;
  affectedProcesses: string[];
  customerImpact: string;
  reputationRisk: string;
  businessContinuity: string;
}

export interface TechnicalImpact {
  level: ImpactLevel;
  description: string;
  systemsAffected: string[];
  dataIntegrity: string;
  performanceImpact: string;
  securityImplications: string;
}

export interface FinancialImpact {
  level: ImpactLevel;
  directCost: number;
  indirectCost: number;
  potentialSavings: number;
  currency: string;
  costBreakdown: CostBreakdownItem[];
}

export interface CostBreakdownItem {
  category: string;
  amount: number;
  description: string;
  recurring: boolean;
}

export interface OperationalImpact {
  level: ImpactLevel;
  description: string;
  resourceRequirements: string[];
  processChanges: string[];
  trainingNeeds: string[];
  maintenanceImpact: string;
}

export interface ComplianceImpact {
  level: ImpactLevel;
  description: string;
  regulationsAffected: string[];
  complianceGaps: string[];
  auditRequirements: string[];
  certificationImpact: string;
}
