/**
 * Flex Layout Component Types
 * 
 * Type definitions for Flexbox layout components including flex containers,
 * flex items, and responsive breakpoint configurations. These types enable
 * flexible, responsive layouts using CSS Flexbox.
 * 
 * Generated by CC on 2025-07-19
 */

import { BaseComponentProps, ContainerComponentProps } from '../shared';

// Flex layout component types
export interface FlexProps extends ContainerComponentProps {
  direction?: 'row' | 'column' | 'row-reverse' | 'column-reverse';
  wrap?: boolean | 'wrap' | 'nowrap' | 'wrap-reverse';
  align?: 'start' | 'center' | 'end' | 'stretch' | 'baseline';
  justify?: 'start' | 'center' | 'end' | 'between' | 'around' | 'evenly';
  gap?: number | string;
  rowGap?: number | string;
  columnGap?: number | string;
  flex?: number | string;
  grow?: number;
  shrink?: number;
  basis?: number | string;
  inline?: boolean;
  responsive?: boolean;
  breakpoints?: FlexBreakpoints;
  equalWidths?: boolean;
  equalHeights?: boolean;
  reverse?: boolean;
  vertical?: boolean;
  horizontal?: boolean;
}

export interface FlexItemProps extends BaseComponentProps {
  flex?: number | string;
  grow?: number;
  shrink?: number;
  basis?: number | string;
  alignSelf?: 'auto' | 'start' | 'center' | 'end' | 'stretch' | 'baseline';
  order?: number;
  responsive?: boolean;
  breakpoints?: FlexItemBreakpoints;
  minWidth?: number | string;
  maxWidth?: number | string;
  minHeight?: number | string;
  maxHeight?: number | string;
}

// Flex breakpoint configuration types
export interface FlexBreakpoints {
  xs?: FlexConfig;
  sm?: FlexConfig;
  md?: FlexConfig;
  lg?: FlexConfig;
  xl?: FlexConfig;
  xxl?: FlexConfig;
}

export interface FlexConfig {
  direction?: string;
  wrap?: boolean | string;
  align?: string;
  justify?: string;
  gap?: number | string;
  flex?: number | string;
  grow?: number;
  shrink?: number;
  basis?: number | string;
}

export interface FlexItemBreakpoints {
  xs?: FlexItemConfig;
  sm?: FlexItemConfig;
  md?: FlexItemConfig;
  lg?: FlexItemConfig;
  xl?: FlexItemConfig;
  xxl?: FlexItemConfig;
}

export interface FlexItemConfig {
  flex?: number | string;
  grow?: number;
  shrink?: number;
  basis?: number | string;
  alignSelf?: string;
  order?: number;
}