/**
 * Grid System Component Types
 *
 * Type definitions for CSS Grid layout components including containers, items,
 * breakpoint configurations, and layout management. These types enable powerful
 * grid-based layouts with responsive capabilities.
 *
 * Generated by CC on 2025-07-19
 */

import type { BaseComponentProps, ContainerComponentProps } from '../shared';

// Grid system component types
export interface GridContainerProps extends ContainerComponentProps {
  columns?: number | string;
  rows?: number | string;
  gap?: number | string;
  columnGap?: number | string;
  rowGap?: number | string;
  templateColumns?: string;
  templateRows?: string;
  templateAreas?: string;
  autoColumns?: string;
  autoRows?: string;
  autoFlow?: 'row' | 'column' | 'row dense' | 'column dense';
  alignItems?: 'start' | 'center' | 'end' | 'stretch' | 'baseline';
  alignContent?: 'start' | 'center' | 'end' | 'stretch' | 'space-between' | 'space-around' | 'space-evenly';
  justifyItems?: 'start' | 'center' | 'end' | 'stretch';
  justifyContent?: 'start' | 'center' | 'end' | 'stretch' | 'space-between' | 'space-around' | 'space-evenly';
  placeItems?: string;
  placeContent?: string;
  responsive?: boolean;
  breakpoints?: GridBreakpoints;
  minColumnWidth?: number | string;
  maxColumnWidth?: number | string;
  equalHeights?: boolean;
  masonry?: boolean;
  masonryColumnWidth?: number | string;
  virtualized?: boolean;
  virtualizedHeight?: number;
  virtualizedItemHeight?: number;
  virtualizedOverscan?: number;
  onLayoutChange?: (layout: GridLayout[]) => void;
}

export interface GridItemProps extends BaseComponentProps {
  column?: number | string;
  row?: number | string;
  columnSpan?: number | string;
  rowSpan?: number | string;
  columnStart?: number | string;
  columnEnd?: number | string;
  rowStart?: number | string;
  rowEnd?: number | string;
  area?: string;
  alignSelf?: 'start' | 'center' | 'end' | 'stretch' | 'baseline';
  justifySelf?: 'start' | 'center' | 'end' | 'stretch';
  placeSelf?: string;
  order?: number;
  responsive?: boolean;
  breakpoints?: GridItemBreakpoints;
  minWidth?: number | string;
  maxWidth?: number | string;
  minHeight?: number | string;
  maxHeight?: number | string;
  aspectRatio?: number | string;
  resizable?: boolean;
  draggable?: boolean;
  onResize?: (size: { width: number; height: number }) => void;
  onDrag?: (position: { x: number; y: number }) => void;
  onDragStart?: () => void;
  onDragEnd?: () => void;
}

// Grid breakpoint configuration types
export interface GridBreakpoints {
  xs?: GridContainerConfig;
  sm?: GridContainerConfig;
  md?: GridContainerConfig;
  lg?: GridContainerConfig;
  xl?: GridContainerConfig;
  xxl?: GridContainerConfig;
}

export interface GridContainerConfig {
  columns?: number | string;
  gap?: number | string;
  templateColumns?: string;
  templateRows?: string;
  autoColumns?: string;
  autoRows?: string;
  alignItems?: string;
  alignContent?: string;
  justifyItems?: string;
  justifyContent?: string;
}

export interface GridItemBreakpoints {
  xs?: GridItemConfig;
  sm?: GridItemConfig;
  md?: GridItemConfig;
  lg?: GridItemConfig;
  xl?: GridItemConfig;
  xxl?: GridItemConfig;
}

export interface GridItemConfig {
  column?: number | string;
  row?: number | string;
  columnSpan?: number | string;
  rowSpan?: number | string;
  columnStart?: number | string;
  columnEnd?: number | string;
  rowStart?: number | string;
  rowEnd?: number | string;
  area?: string;
  alignSelf?: string;
  justifySelf?: string;
  order?: number;
}

// Grid layout configuration
export interface GridLayout {
  i: string;
  x: number;
  y: number;
  w: number;
  h: number;
  minW?: number;
  maxW?: number;
  minH?: number;
  maxH?: number;
  static?: boolean;
  isDraggable?: boolean;
  isResizable?: boolean;
  resizeHandles?: string[];
  isBounded?: boolean;
}
