/**
 * Layout Hooks & Context Types
 * 
 * Type definitions for layout-related React hooks and context providers.
 * These types support responsive behavior, layout state management,
 * and grid/masonry layout functionality.
 * 
 * Generated by CC on 2025-07-19
 */

import type { UserInfo } from './navigation-user-types'
import type { NotificationItem } from './navigation-user-types'
import type { GridLayout } from './grid-types';
import type { MasonryLayout } from './stack-masonry-types';

// Layout hooks types
export interface UseLayoutReturn {
  breakpoint: string;
  width: number;
  height: number;
  isMobile: boolean;
  isTablet: boolean;
  isDesktop: boolean;
  isLargeDesktop: boolean;
  orientation: 'portrait' | 'landscape';
}

export interface UseResponsiveReturn {
  matches: boolean;
  breakpoint: string;
  width: number;
  height: number;
}

export interface UseGridReturn {
  layout: GridLayout[];
  setLayout: (layout: GridLayout[]) => void;
  addItem: (item: Omit<GridLayout, 'x' | 'y'>) => void;
  removeItem: (itemId: string) => void;
  updateItem: (itemId: string, updates: Partial<GridLayout>) => void;
  resetLayout: () => void;
  compactLayout: () => void;
}

export interface UseMasonryReturn {
  layout: MasonryLayout[];
  columns: number;
  heights: number[];
  addItem: (height: number) => void;
  removeItem: (index: number) => void;
  resetLayout: () => void;
  recalculateLayout: () => void;
}

// Layout context types
export interface LayoutContext {
  breakpoint: string;
  width: number;
  height: number;
  isMobile: boolean;
  isTablet: boolean;
  isDesktop: boolean;
  orientation: 'portrait' | 'landscape';
  theme: 'light' | 'dark' | 'auto';
  colorScheme: string;
  direction: 'ltr' | 'rtl';
  compact: boolean;
  sidebarCollapsed: boolean;
  setSidebarCollapsed: (collapsed: boolean) => void;
  toggleSidebar: () => void;
}

export interface AdminLayoutContext extends LayoutContext {
  user: UserInfo | null;
  notifications: NotificationItem[];
  unreadNotifications: number;
  markNotificationAsRead: (id: string) => void;
  markAllNotificationsAsRead: () => void;
  addNotification: (notification: Omit<NotificationItem, 'id' | 'timestamp'>) => void;
  removeNotification: (id: string) => void;
}