  onLogout?: () => void;
  theme?: 'light' | 'dark' | 'auto';
  colorScheme?: string;
  compact?: boolean;
  fullWidth?: boolean;
  bordered?: boolean;
  showBreadcrumbs?: boolean;
  showNotifications?: boolean;
  showUserMenu?: boolean;
  showQuickActions?: boolean;
  showSearch?: boolean;
  showHelp?: boolean;
  showFeedback?: boolean;
