/**
 * Navigation & User Interface Types
 *
 * Type definitions for navigation components, user information, and related
 * UI elements used in layout components. These types support user menus,
 * navigation items, breadcrumbs, notifications, and quick actions.
 *
 * Generated by CC on 2025-07-19
 */

import type { ReactNode } from 'react';

// Supporting types for navigation and user interface
export interface UserInfo {
  id: string;
  name: string;
  email: string;
  avatar?: string;
  role: string;
  permissions: string[];
  lastLogin?: string;
  status: 'online' | 'offline' | 'away' | 'busy';
}

export interface NavigationItem {
  id: string;
  label: string;
  icon?: ReactNode;
  href?: string;
  onClick?: () => void;
  active?: boolean;
  disabled?: boolean;
  badge?: string | number;
  children?: NavigationItem[];
  metadata?: Record<string, string | number | boolean | null>;
}

export interface BreadcrumbItem {
  id: string;
  label: string;
  href?: string;
  onClick?: () => void;
  active?: boolean;
  disabled?: boolean;
  icon?: ReactNode;
  metadata?: Record<string, string | number | boolean | null>;
}

export interface NotificationItem {
  id: string;
  title: string;
  message: string;
  type: 'info' | 'success' | 'warning' | 'error';
  read: boolean;
  timestamp: string;
  action?: {
    label: string;
    onClick: () => void;
  };
  metadata?: Record<string, string | number | boolean | null>;
}

export interface UserMenuItem {
  id: string;
  label: string;
  icon?: ReactNode;
  href?: string;
  onClick?: () => void;
  disabled?: boolean;
  divider?: boolean;
  metadata?: Record<string, string | number | boolean | null>;
}

export interface QuickAction {
  id: string;
  label: string;
  icon: ReactNode;
  onClick: () => void;
  disabled?: boolean;
  tooltip?: string;
  shortcut?: string;
  metadata?: Record<string, string | number | boolean | null>;
}
