/**
 * Responsive Component Types
 *
 * Type definitions for responsive layout components including responsive
 * containers and media query components. These types enable conditional
 * rendering and layout adjustments based on screen size and device capabilities.
 *
 * Generated by CC on 2025-07-19
 */

import type { ReactNode } from 'react';

// Responsive component types
export interface ResponsiveProps {
  breakpoint?: string | number;
  minBreakpoint?: string | number;
  maxBreakpoint?: string | number;
  hideBelow?: string | number;
  hideAbove?: string | number;
  showOnly?: string | number | Array<string | number>;
  children: ReactNode | ((breakpoint: string, width: number) => ReactNode);
  className?: string;
  id?: string;
}

export interface MediaQueryProps {
  query: string;
  children: ReactNode | ((matches: boolean) => ReactNode);
  fallback?: ReactNode;
  serverFallback?: boolean;
  className?: string;
  id?: string;
}
