/**
 * Stack & Masonry Layout Component Types
 *
 * Type definitions for stack layout components (VStack, HStack) and masonry
 * layouts. Stack layouts provide simple vertical/horizontal arrangements,
 * while masonry layouts create Pinterest-style grid arrangements.
 *
 * Generated by CC on 2025-07-19
 */

import type { ReactNode } from 'react';
import type { BaseComponentProps, ContainerComponentProps } from '../shared';

// Stack layout component types
export interface StackProps extends ContainerComponentProps {
  direction?: 'vertical' | 'horizontal';
  spacing?: number | string;
  align?: 'start' | 'center' | 'end' | 'stretch' | 'baseline';
  justify?: 'start' | 'center' | 'end' | 'between' | 'around' | 'evenly';
  wrap?: boolean;
  reverse?: boolean;
  responsive?: boolean;
  breakpoints?: StackBreakpoints;
  divider?: ReactNode;
  dividerStyle?: React.CSSProperties;
  dividerClassName?: string;
}

export interface VStackProps extends Omit<StackProps, 'direction'> {
  direction?: never;
}

export interface HStackProps extends Omit<StackProps, 'direction'> {
  direction?: never;
}

// Masonry layout component types
export interface MasonryProps extends BaseComponentProps {
  columns?: number;
  spacing?: number | string;
  responsive?: boolean;
  breakpoints?: MasonryBreakpoints;
  minColumnWidth?: number;
  maxColumnWidth?: number;
  children: ReactNode[];
  itemComponent?: React.ComponentType<MasonryItemProps>;
  onLayoutChange?: (layout: MasonryLayout[]) => void;
  virtualized?: boolean;
  virtualizedHeight?: number;
  virtualizedOverscan?: number;
  sequential?: boolean;
}

export interface MasonryItemProps extends BaseComponentProps {
  index: number;
  column: number;
  height?: number;
  children: ReactNode;
}

// Breakpoint configuration types
export interface StackBreakpoints {
  xs?: StackConfig;
  sm?: StackConfig;
  md?: StackConfig;
  lg?: StackConfig;
  xl?: StackConfig;
  xxl?: StackConfig;
}

export interface StackConfig {
  direction?: 'vertical' | 'horizontal';
  spacing?: number | string;
  align?: string;
  justify?: string;
  wrap?: boolean;
  reverse?: boolean;
}

export interface MasonryBreakpoints {
  xs?: MasonryConfig;
  sm?: MasonryConfig;
  md?: MasonryConfig;
  lg?: MasonryConfig;
  xl?: MasonryConfig;
  xxl?: MasonryConfig;
}

export interface MasonryConfig {
  columns?: number;
  spacing?: number | string;
  minColumnWidth?: number;
  maxColumnWidth?: number;
}

export interface MasonryLayout {
  index: number;
  column: number;
  x: number;
  y: number;
  width: number;
  height: number;
}
